import React, { useState, useEffect, useRef } from 'react';
import { audioService, AudioData } from '../lib/supabase';
import { supabase } from '../lib/supabase';

interface AudioTimer {
  interval: number;
  timeLeft: number;
  intervalId: NodeJS.Timeout;
}

interface AudioPlayerProps {
  onEnded: () => void;
  isPlaying: boolean;
  setIsPlaying: (playing: boolean) => void;
}

const AudioPlayer: React.FC<AudioPlayerProps> = ({ onEnded, isPlaying, setIsPlaying }) => {
  const [audios, setAudios] = useState<AudioData[]>([]);
  const [currentAudio, setCurrentAudio] = useState<AudioData | null>(null);
  const [progress, setProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [audioTimers, setAudioTimers] = useState<Record<string, AudioTimer>>({});
  const [showIntervalInput, setShowIntervalInput] = useState<string | null>(null);
  const timersRef = useRef<Record<string, NodeJS.Timeout>>({});
  const [timerAudioId, setTimerAudioId] = useState<string | null>(null);
  const [pendingAudioId, setPendingAudioId] = useState<string | null>(null);
  const [pendingAudios, setPendingAudios] = useState<string[]>([]);
  const playAttemptRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    loadAudios();
  }, []);

  useEffect(() => {
    const playAudio = async () => {
      if (!audioRef.current) return;

      try {
        console.log('Tentando reproduzir áudio...');
        await audioRef.current.play();
        console.log('Áudio iniciado com sucesso');
        window.dispatchEvent(new Event('audioPlay'));
      } catch (error: unknown) {
        console.error('Erro ao reproduzir áudio:', error);
        if (error instanceof Error && error.name === 'AbortError') {
          // Limpa qualquer tentativa pendente
          if (playAttemptRef.current) {
            clearTimeout(playAttemptRef.current);
          }
          
          // Tenta novamente após um delay
          playAttemptRef.current = setTimeout(async () => {
            try {
              console.log('Tentando reproduzir novamente...');
              await audioRef.current?.play();
              console.log('Áudio iniciado na segunda tentativa');
              window.dispatchEvent(new Event('audioPlay'));
            } catch (retryError) {
              console.error('Erro na segunda tentativa:', retryError);
              setIsPlaying(false);
            }
          }, 500);
        } else {
          setIsPlaying(false);
        }
      }
    };

    if (audioRef.current) {
      if (isPlaying) {
        // Limpa qualquer tentativa pendente antes de iniciar uma nova
        if (playAttemptRef.current) {
          clearTimeout(playAttemptRef.current);
          playAttemptRef.current = null;
        }
        
        // Adiciona um pequeno delay antes de tentar reproduzir
        playAttemptRef.current = setTimeout(() => {
          playAudio();
        }, 100);
      } else {
        // Limpa qualquer tentativa pendente
        if (playAttemptRef.current) {
          clearTimeout(playAttemptRef.current);
          playAttemptRef.current = null;
        }
        audioRef.current.pause();
        window.dispatchEvent(new Event('audioStop'));
      }
    }

    // Cleanup
    return () => {
      if (playAttemptRef.current) {
        clearTimeout(playAttemptRef.current);
        playAttemptRef.current = null;
      }
    };
  }, [isPlaying]);

  useEffect(() => {
    return () => {
      Object.values(timersRef.current).forEach(timer => {
        clearInterval(timer);
      });
    };
  }, []);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.onended = () => {
        console.log('Áudio terminou (evento nativo)');
        handleAudioEnd();
      };

      audioRef.current.onloadeddata = () => {
        if (isPlaying) {
          audioRef.current?.play().catch(error => {
            console.error('Erro ao reproduzir áudio após carregamento:', error);
            setIsPlaying(false);
          });
        }
      };
    }
  }, [audioRef.current]);

  useEffect(() => {
    Object.entries(audioTimers).forEach(([audioId, timer]) => {
      if (timer.timeLeft === 0) {
        setAudioTimers(prev => ({
          ...prev,
          [audioId]: {
            ...timer,
            timeLeft: timer.interval
          }
        }));

        if (!isPlaying) {
          const audio = audios.find(a => a.id === audioId);
          if (audio && audioRef.current) {
            const playWithDelay = async () => {
              try {
                await new Promise(resolve => setTimeout(resolve, 500));
                setCurrentAudio(audio);
                
                if (audioRef.current) {
                  audioRef.current.currentTime = 0;
                  console.log('Tentando reproduzir áudio automático:', audio.title);
                  await audioRef.current.play();
                  setIsPlaying(true);
                  console.log('Áudio automático iniciado com sucesso');
                }
              } catch (error) {
                console.error('Erro ao reproduzir áudio automático:', error);
                if (error instanceof Error && error.name === 'AbortError') {
                  console.log('Tentando novamente em 1 segundo...');
                  setTimeout(async () => {
                    try {
                      if (audioRef.current) {
                        await audioRef.current.play();
                        setIsPlaying(true);
                        console.log('Áudio automático iniciado na segunda tentativa');
                      }
                    } catch (retryError) {
                      console.error('Falha na segunda tentativa:', retryError);
                      console.log('Adicionando à fila de pendentes após falha');
                      setPendingAudios(prev => 
                        prev.includes(audioId) ? prev : [...prev, audioId]
                      );
                    }
                  }, 1000);
                }
              }
            };

            playWithDelay();
          }
        } else {
          console.log('Adicionando áudio à fila de pendentes:', audioId);
          setPendingAudios(prev => {
            if (prev.includes(audioId)) return prev;
            console.log('Fila atualizada:', [...prev, audioId]);
            return [...prev, audioId];
          });
        }
      }
    });
  }, [audioTimers, audios, isPlaying]);

  const loadAudios = async () => {
    try {
      const data = await audioService.getAudios();
      console.log('Áudios carregados:', data); // Para debug
      setAudios(data);
    } catch (error) {
      console.error('Erro ao carregar áudios:', error);
    }
  };

  const handleTimeUpdate = () => {
    if (audioRef.current) {
      const progress = (audioRef.current.currentTime / audioRef.current.duration) * 100;
      setProgress(progress);
    }
  };

  const handleProgressClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (audioRef.current) {
      const bounds = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - bounds.left;
      const width = bounds.width;
      const percentage = x / width;
      audioRef.current.currentTime = audioRef.current.duration * percentage;
    }
  };

  const handleAudioEnd = async () => {
    console.log('Áudio terminou, verificando pendentes...');
    console.log('Fila de pendentes:', pendingAudios);
    
    if (playAttemptRef.current) {
      clearTimeout(playAttemptRef.current);
      playAttemptRef.current = null;
    }

    if (audioRef.current) {
      audioRef.current.currentTime = 0;
    }
    setProgress(0);

    // Adiciona um delay antes de verificar a fila de pendentes
    await new Promise(resolve => setTimeout(resolve, 300));

    if (pendingAudios.length > 0) {
      try {
        console.log('Existem áudios pendentes, reproduzindo próximo...');
        const nextAudioId = pendingAudios[0];
        const nextAudio = audios.find(a => a.id === nextAudioId);

        setPendingAudios(prev => prev.slice(1));

        if (nextAudio) {
          console.log('Iniciando reprodução do áudio pendente:', nextAudio.title);
          // Aumenta o delay antes de iniciar o próximo áudio
          await new Promise(resolve => setTimeout(resolve, 800));
          setCurrentAudio(nextAudio);
          setIsPlaying(true);
        }
      } catch (error) {
        console.error('Erro ao reproduzir próximo áudio:', error);
        setIsPlaying(false);
        onEnded();
      }
    } else {
      console.log('Não há áudios pendentes');
      setIsPlaying(false);
      onEnded();
    }

    window.dispatchEvent(new Event('audioStop'));
  };

  const togglePlay = (audio: AudioData) => {
    if (currentAudio?.id === audio.id) {
      setIsPlaying(!isPlaying);
    } else {
      console.log('Mudando para novo áudio manualmente');
      setCurrentAudio(audio);
      if (audioRef.current) {
        audioRef.current.currentTime = 0;
      }
      setIsPlaying(true);
      // Não vamos mais limpar a fila de pendentes ao tocar manualmente
      // setPendingAudios([]);
    }
  };

  const handleUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    try {
      console.log('Iniciando upload do arquivo:', file.name);
      
      // Upload do arquivo
      const fileExt = file.name.split('.').pop();
      const fileName = `${Math.random()}.${fileExt}`;
      const filePath = `audios/${fileName}`;

      console.log('Tentando fazer upload para:', filePath);

      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('media')
        .upload(filePath, file);

      if (uploadError) {
        console.error('Erro no upload:', uploadError);
        throw uploadError;
      }

      console.log('Upload bem sucedido:', uploadData);

      // Obtém a URL pública
      const { data: { publicUrl } } = supabase.storage
        .from('media')
        .getPublicUrl(filePath);

      console.log('URL pública gerada:', publicUrl);

      // Adiciona ao banco de dados
      const newAudio = await audioService.addAudio({
        title: file.name,
        url: publicUrl,
        is_auto_play: false
      });

      console.log('Áudio adicionado ao banco:', newAudio);

      // Recarrega todos os áudios
      await loadAudios();
      
      // Seleciona o novo áudio
      setCurrentAudio(newAudio);
      
      // Limpa o input
      event.target.value = '';

    } catch (error) {
      console.error('Erro detalhado no upload:', error);
    } finally {
      setIsUploading(false);
    }
  };

  const startRepeatTimer = (audioId: string, minutes: number) => {
    // Não vamos mais alterar o áudio atual ou seu estado de reprodução
    // Apenas armazenamos qual áudio será reproduzido quando o timer zerar
    setTimerAudioId(audioId);

    if (timersRef.current[audioId]) {
      clearInterval(timersRef.current[audioId]);
    }

    const seconds = minutes * 60;
    
    const intervalId = setInterval(() => {
      setAudioTimers(prev => {
        const currentTimer = prev[audioId];
        if (!currentTimer) return prev;

        const newTimeLeft = currentTimer.timeLeft - 1;
        return {
          ...prev,
          [audioId]: {
            ...currentTimer,
            timeLeft: newTimeLeft
          }
        };
      });
    }, 1000);

    timersRef.current[audioId] = intervalId;

    setAudioTimers(prev => ({
      ...prev,
      [audioId]: {
        interval: seconds,
        timeLeft: seconds,
        intervalId
      }
    }));

    setShowIntervalInput(null);
  };

  const stopRepeatTimer = (audioId: string) => {
    if (timersRef.current[audioId]) {
      clearInterval(timersRef.current[audioId]);
      delete timersRef.current[audioId];
    }

    setAudioTimers(prev => {
      const newTimers = { ...prev };
      delete newTimers[audioId];
      return newTimers;
    });
  };

  const handleDelete = async (audioId: string) => {
    try {
      await audioService.deleteAudio(audioId);
      if (currentAudio?.id === audioId) {
        setCurrentAudio(null);
        setIsPlaying(false);
      }
      await loadAudios();
    } catch (error) {
      console.error('Erro ao deletar áudio:', error);
    }
  };

  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg p-3">
      {/* Player Central */}
      <div className="mb-3 bg-[#2d2d2d] rounded-lg p-4 border border-[#404040]">
        {/* Título do Áudio Centralizado */}
        <div className="mb-4 text-center">
          <h3 className="text-[#e1aa1e] font-medium">
            {currentAudio?.title || 'Selecione um áudio'}
          </h3>
        </div>

        <audio
          ref={audioRef}
          src={currentAudio?.url}
          onTimeUpdate={handleTimeUpdate}
          onEnded={handleAudioEnd}
          className="hidden"
        />

        {/* Barra de Progresso */}
        <div
          className="h-1 bg-[#404040] rounded-full cursor-pointer"
          onClick={handleProgressClick}
        >
          <div
            className="h-full bg-[#e1aa1e] rounded-full"
            style={{ width: `${progress}%` }}
          />
        </div>

        {/* Tempo e Próxima Reprodução */}
        <div className="flex justify-between items-center mt-2">
          {/* Tempo Atual/Total */}
          <div className="text-xs text-gray-400">
            {formatTime(audioRef.current?.currentTime || 0)} / {formatTime(audioRef.current?.duration || 0)}
          </div>

          {/* Próxima Reprodução */}
          {pendingAudios.length > 0 && (
            <div className="text-right">
              <div className="text-xs text-gray-400">
                Próximo: {audios.find(a => a.id === pendingAudios[0])?.title}
              </div>
              {audioTimers[pendingAudios[0]] && (
                <div className="text-xs text-[#e1aa1e]">
                  Em {audioTimers[pendingAudios[0]].timeLeft}s
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Painel de Controle */}
      <div className="mb-3 bg-[#2d2d2d] rounded-lg p-2 border border-[#404040] flex justify-between items-center">
        <span className="text-sm text-gray-400 px-2">Lista de Áudios</span>
        <label className="bg-[#e1aa1e] hover:bg-[#e1aa1e]/80 text-gray-900 px-3 py-1.5 rounded cursor-pointer transition-colors text-sm">
          {isUploading ? 'Enviando...' : 'Anexar Áudio'}
          <input
            type="file"
            className="hidden"
            accept="audio/*"
            onChange={handleUpload}
            disabled={isUploading}
          />
        </label>
      </div>

      {/* Lista de Áudios */}
      <div className="space-y-2 max-h-[400px] overflow-y-auto">
        {audios.map((audio) => (
          <div
            key={audio.id}
            className={`flex items-center gap-3 p-2 rounded-lg hover:bg-[#2d2d2d] transition-colors ${
              currentAudio?.id === audio.id ? 'bg-[#2d2d2d]' : ''
            }`}
          >
            <button
              onClick={() => togglePlay(audio)}
              className="p-1 hover:bg-[#404040] rounded-full transition-colors"
            >
              <svg
                className="w-6 h-6 text-[#e1aa1e]"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                {isPlaying && currentAudio?.id === audio.id ? (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M10 9v6m4-6v6m7-3a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                ) : (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                )}
              </svg>
            </button>
            <span className="flex-1 truncate">{audio.title}</span>
            
            {/* Controles do item */}
            <div className="flex items-center gap-2">
              <div className="flex items-center gap-1">
                {showIntervalInput === audio.id ? (
                  <div className="flex items-center gap-1">
                    <input
                      type="number"
                      min="1"
                      placeholder="Min"
                      className="w-16 px-2 py-1 bg-[#2d2d2d] border border-[#404040] rounded text-gray-200 [appearance:textfield] [&::-webkit-outer-spin-button]:appearance-none [&::-webkit-inner-spin-button]:appearance-none"
                      onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                          const value = parseInt((e.target as HTMLInputElement).value);
                          if (value > 0) startRepeatTimer(audio.id, value);
                        }
                      }}
                      autoFocus
                    />
                    <button
                      onClick={() => {
                        const input = document.querySelector('input[type="number"]') as HTMLInputElement;
                        const value = parseInt(input.value);
                        if (value > 0) startRepeatTimer(audio.id, value);
                      }}
                      className="text-[#e1aa1e] hover:text-[#e1aa1e]/80 text-sm px-2 py-1"
                    >
                      OK
                    </button>
                  </div>
                ) : (
                  <>
                    <button
                      onClick={() => {
                        if (audioTimers[audio.id]) {
                          stopRepeatTimer(audio.id);
                        } else {
                          setShowIntervalInput(audio.id);
                        }
                      }}
                      className={`p-1 hover:bg-[#404040] rounded-full transition-colors ${
                        audioTimers[audio.id] ? 'text-[#e1aa1e]' : 'text-gray-400'
                      }`}
                      title={audioTimers[audio.id] ? 'Parar repetição' : 'Repetir'}
                    >
                      <svg
                        className="w-5 h-5"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"
                        />
                      </svg>
                    </button>
                    {audioTimers[audio.id] && (
                      <span className="text-xs text-[#e1aa1e]">
                        {formatTime(audioTimers[audio.id].timeLeft)}
                      </span>
                    )}
                  </>
                )}
              </div>

              <button
                onClick={() => handleDelete(audio.id)}
                className="p-1 hover:bg-[#404040] rounded-full transition-colors text-red-400 hover:text-red-300"
              >
                <svg
                  className="w-5 h-5"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                  />
                </svg>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AudioPlayer;
