import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL!;
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY!;

export const supabase = createClient(supabaseUrl, supabaseAnonKey);

export interface AudioData {
  id: string;
  title: string;
  url: string;
  interval?: number;
  isAutoPlay?: boolean;
  created_at: string;
}

export interface VideoData {
  id: string;
  url: string;
  title?: string;
  created_at: string;
}

export const audioService = {
  async getAudios() {
    const { data, error } = await supabase
      .from('audios')
      .select('*')
      .order('created_at', { ascending: false });
    
    if (error) {
      console.error('Erro ao buscar áudios:', error);
      throw error;
    }

    console.log('Dados retornados do Supabase:', data); // Para debug
    return data as AudioData[];
  },

  async addAudio(audio: Omit<AudioData, 'id' | 'created_at'>) {
    const { data, error } = await supabase
      .from('audios')
      .insert([audio])
      .select()
      .single();
    
    if (error) {
      console.error('Erro ao adicionar áudio:', error);
      throw error;
    }

    if (!data) {
      throw new Error('Nenhum dado retornado após inserção');
    }

    return data as AudioData;
  },

  async updateAudio(id: string, updates: Partial<AudioData>) {
    const { data, error } = await supabase
      .from('audios')
      .update(updates)
      .eq('id', id)
      .select();
    
    if (error) throw error;
    return data[0] as AudioData;
  },

  async deleteAudio(id: string) {
    const { error } = await supabase
      .from('audios')
      .delete()
      .eq('id', id);
    
    if (error) throw error;
  }
};

export const videoService = {
  async getRecentVideos() {
    const { data, error } = await supabase
      .from('videos')
      .select('*')
      .order('created_at', { ascending: false })
      .limit(20);
    
    if (error) {
      console.error('Erro ao buscar vídeos:', error);
      throw new Error(`Erro ao buscar vídeos: ${error.message}`);
    }
    console.log('Vídeos carregados:', data);
    return data as VideoData[];
  },

  async addVideo(url: string, title: string) {
    if (!url) {
      throw new Error('URL não pode estar vazia');
    }

    console.log('Tentando adicionar vídeo:', { url, title });
    
    const { data, error } = await supabase
      .from('videos')
      .insert([{ url, title }])
      .select()
      .single();
    
    if (error) {
      console.error('Erro detalhado do Supabase:', error);
      throw new Error(
        error.message || 
        'Erro ao adicionar vídeo no banco de dados'
      );
    }

    if (!data) {
      throw new Error('Nenhum dado retornado após inserção');
    }

    console.log('Vídeo adicionado com sucesso:', data);
    return data as VideoData;
  },

  async deleteVideo(id: string) {
    const { error } = await supabase
      .from('videos')
      .delete()
      .eq('id', id);
    
    if (error) {
      throw new Error(`Erro ao deletar vídeo: ${error.message}`);
    }
  }
};
