import React, { useState, useRef, useEffect } from 'react';

interface TextToSpeechProps {
  apiKey: string;
}

const TextToSpeech: React.FC<TextToSpeechProps> = ({ apiKey }) => {
  const [text, setText] = useState('');
  const [audioUrl, setAudioUrl] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [lastGeneratedText, setLastGeneratedText] = useState<string>('');
  const audioRef = useRef<HTMLAudioElement>(null);

  const notifyAudioStart = () => {
    window.dispatchEvent(new Event('audioPlay'));
  };

  const notifyAudioEnd = () => {
    window.dispatchEvent(new Event('audioStop'));
  };

  const playExistingAudio = () => {
    if (audioRef.current) {
      audioRef.current.currentTime = 0;
      audioRef.current.play();
    }
  };

  const generateSpeech = async () => {
    if (!text.trim()) {
      setError('Por favor, digite algum texto');
      return;
    }

    // Se o texto é o mesmo que já foi gerado, apenas reproduz novamente
    if (text === lastGeneratedText && audioUrl) {
      playExistingAudio();
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `https://texttospeech.googleapis.com/v1/text:synthesize?key=${apiKey}`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            input: {
              text,
              // Adiciona SSML para melhorar a naturalidade
              ssml: `<speak>
                <prosody rate="0.95" pitch="+0.5" volume="loud">
                  ${text.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;')}
                </prosody>
              </speak>`
            },
            voice: {
              languageCode: 'pt-BR',
              name: 'pt-BR-Wavenet-D',
              ssmlGender: 'FEMALE',
            },
            audioConfig: {
              audioEncoding: 'MP3',
              pitch: 0.5, // Ajuste sutil no pitch para mais naturalidade
              speakingRate: 0.95, // Velocidade levemente mais lenta
              volumeGainDb: 2.0, // Volume um pouco mais alto
              effectsProfileId: ['large-home-entertainment-class-device'], // Melhor qualidade de áudio
              // Adiciona parâmetros de otimização
              sampleRateHertz: 48000, // Alta qualidade de áudio
              // Ajustes de ênfase
              emphasis: {
                level: 'moderate',
                type: 'auto'
              }
            },
          }),
        }
      );

      if (!response.ok) {
        throw new Error('Erro ao gerar áudio');
      }

      const { audioContent } = await response.json();
      const audioBlob = new Blob(
        [Uint8Array.from(atob(audioContent), c => c.charCodeAt(0))],
        { type: 'audio/mp3' }
      );
      const url = URL.createObjectURL(audioBlob);
      setAudioUrl(url);
      setLastGeneratedText(text);
      
      if (audioRef.current) {
        audioRef.current.src = url;
        audioRef.current.addEventListener('play', notifyAudioStart);
        audioRef.current.addEventListener('ended', notifyAudioEnd);
        audioRef.current.addEventListener('pause', notifyAudioEnd);
        audioRef.current.play();
      }
    } catch (err) {
      setError('Erro ao gerar áudio. Tente novamente.');
    } finally {
      setIsLoading(false);
    }
  };

  // Limpa a URL do áudio anterior quando o componente é desmontado
  useEffect(() => {
    return () => {
      if (audioUrl) {
        URL.revokeObjectURL(audioUrl);
      }
      if (audioRef.current) {
        audioRef.current.removeEventListener('play', notifyAudioStart);
        audioRef.current.removeEventListener('ended', notifyAudioEnd);
        audioRef.current.removeEventListener('pause', notifyAudioEnd);
      }
    };
  }, [audioUrl]);

  const handleDownload = () => {
    if (audioUrl) {
      const a = document.createElement('a');
      a.href = audioUrl;
      a.download = 'audio.mp3';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  return (
    <div className="max-w-2xl mx-auto bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg overflow-hidden">
      <div className="bg-[#2d2d2d] px-6 py-4 border-b border-[#404040] flex items-center gap-3">
        <svg 
          className="w-5 h-5 text-[#e1aa1e]" 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
        >
          <path 
            strokeLinecap="round" 
            strokeLinejoin="round" 
            strokeWidth={2} 
            d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" 
          />
        </svg>
        <h2 className="text-lg font-medium text-[#e1aa1e]">
          Digite o texto para falar
        </h2>
      </div>

      <div className="p-6 space-y-4">
        <div className="relative">
          <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Digite aqui o texto que você quer que seja falado..."
            className="w-full h-24 bg-[#2d2d2d] border border-[#404040] text-gray-200 rounded-lg px-4 py-3 focus:border-[#e1aa1e] focus:outline-none resize-none"
            disabled={isLoading}
          />
          {error && (
            <p className="absolute -bottom-6 left-0 text-red-400 text-sm">{error}</p>
          )}
        </div>

        <div className="flex items-center gap-3 pt-2">
          <button
            onClick={generateSpeech}
            disabled={isLoading}
            className={`flex-1 ${
              isLoading 
                ? 'bg-[#e1aa1e]/50 cursor-not-allowed' 
                : 'bg-[#e1aa1e] hover:bg-[#e1aa1e]/80'
            } text-gray-900 px-4 py-2.5 rounded-lg transition-colors flex items-center justify-center gap-2 font-medium`}
          >
            {isLoading ? (
              <>
                <svg className="animate-spin h-4 w-4" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                </svg>
                <span>Gerando...</span>
              </>
            ) : (
              <>
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  {text === lastGeneratedText && audioUrl ? (
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                  ) : (
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z" />
                  )}
                </svg>
                <span>{text === lastGeneratedText && audioUrl ? 'Repetir Fala' : 'Falar'}</span>
              </>
            )}
          </button>

          {audioUrl && (
            <button
              onClick={handleDownload}
              className="bg-[#2d2d2d] hover:bg-[#404040] border border-[#404040] text-[#e1aa1e] px-4 py-2.5 rounded-lg flex items-center justify-center gap-2 transition-colors font-medium"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" />
              </svg>
              <span>Baixar</span>
            </button>
          )}
        </div>
      </div>

      <audio ref={audioRef} className="hidden" />
    </div>
  );
};

export default TextToSpeech;
