import React, { useState, useEffect } from 'react';
import { videoService, VideoData } from '../lib/supabase';
import VideoSidebar from './VideoSidebar';

interface VideoPlayerProps {
  videoId?: string;
  onEnded: () => void;
  isPlaying: boolean;
  setIsPlaying: (playing: boolean) => void;
  autoplay?: boolean;
  pendingVideoId?: string;
}

const VideoPlayer: React.FC<VideoPlayerProps> = ({ 
  videoId,
  onEnded, 
  isPlaying, 
  setIsPlaying,
  autoplay = false,
  pendingVideoId 
}) => {
  const [videos, setVideos] = useState<VideoData[]>([]);
  const [newVideoUrl, setNewVideoUrl] = useState('');
  const [error, setError] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [selectedVideo, setSelectedVideo] = useState<VideoData | null>(null);
  const [player, setPlayer] = useState<any>(null);
  const [isPlayerReady, setIsPlayerReady] = useState(false);
  const [currentVideoId, setCurrentVideoId] = useState<string | null>(null);

  // Carrega vídeos quando o componente monta
  useEffect(() => {
    const loadInitialVideos = async () => {
      try {
        const data = await videoService.getRecentVideos();
        setVideos(data);
      } catch (error) {
        console.error('Erro ao carregar vídeos:', error);
      }
    };

    loadInitialVideos();
  }, []);

  // Inicializa o player do YouTube
  useEffect(() => {
    let mounted = true;
    let initAttempts = 0;
    const MAX_ATTEMPTS = 3;

    const checkAudioPlaying = () => {
      const audioElement = document.querySelector('audio');
      return audioElement && !audioElement.paused;
    };

    const loadYouTubeAPI = () => {
      return new Promise<void>((resolve, reject) => {
        if ((window as any).YT) {
          resolve();
          return;
        }

        const tag = document.createElement('script');
        tag.src = 'https://www.youtube.com/iframe_api';
        tag.async = true;
        const firstScriptTag = document.getElementsByTagName('script')[0];
        firstScriptTag.parentNode?.insertBefore(tag, firstScriptTag);

        (window as any).onYouTubeIframeAPIReady = () => {
          resolve();
        };

        setTimeout(() => {
          reject(new Error('Timeout ao carregar API do YouTube'));
        }, 10000);
      });
    };

    const initPlayer = async () => {
      try {
        await loadYouTubeAPI();

        if (!mounted) return;

        // Limpa qualquer iframe existente
        const existingIframe = document.getElementById('youtube-player');
        if (existingIframe) {
          existingIframe.remove();
        }

        // Cria um novo container para o player
        const container = document.createElement('div');
        container.id = 'youtube-player';
        container.className = 'w-full h-full';
        const playerContainer = document.getElementById('player-container');
        if (playerContainer) {
          playerContainer.appendChild(container);
        }

        const isAudioPlaying = checkAudioPlaying();
        console.log('Estado do áudio ao inicializar:', isAudioPlaying ? 'tocando' : 'parado');

        const newPlayer = new (window as any).YT.Player('youtube-player', {
          height: '100%',
          width: '100%',
          videoId: '',
          playerVars: {
            enablejsapi: 1,
            origin: window.location.origin,
            host: window.location.protocol + '//' + window.location.host,
            widget_referrer: window.location.href,
            playsinline: 1,
            controls: 1,
            rel: 0,
            modestbranding: 1,
            autoplay: 0,
            mute: 0
          },
          events: {
            onReady: () => {
              if (!mounted) return;
              console.log('Player pronto');
              setIsPlayerReady(true);
              setPlayer(newPlayer);

              try {
                // Configura volume inicial baseado no estado do áudio
                const initialVolume = isAudioPlaying ? 10 : 100;
                console.log('Configurando volume inicial:', initialVolume);
                newPlayer.setVolume(initialVolume);

                // Carrega o vídeo inicial
                const videoToPlay = selectedVideo || videos[0];
                if (videoToPlay) {
                  const videoId = extractVideoId(videoToPlay.url);
                  if (videoId) {
                    console.log('Carregando vídeo inicial:', videoId);
                    setCurrentVideoId(videoId);
                    // Sempre usa cueVideoById para evitar autoplay
                    newPlayer.cueVideoById({
                      videoId,
                      startSeconds: 0,
                      suggestedQuality: 'large'
                    });
                  }
                }
              } catch (error) {
                console.error('Erro ao configurar player:', error);
              }
            },
            onStateChange: (event: any) => {
              if (!mounted) return;

              const currentState = event.data;
              console.log('Estado do player mudou para:', currentState);

              if (currentState === (window as any).YT.PlayerState.ENDED) {
                setIsPlaying(false);
                onEnded();
              } else if (currentState === (window as any).YT.PlayerState.PLAYING) {
                setIsPlaying(true);
                // Verifica o estado do áudio novamente
                if (checkAudioPlaying()) {
                  console.log('Áudio detectado, ajustando volume do vídeo');
                  fadeVolume(100, 10, 500);
                }
              } else if (currentState === (window as any).YT.PlayerState.PAUSED) {
                setIsPlaying(false);
              }
            },
            onError: (error: any) => {
              if (!mounted) return;
              console.error('Erro no player:', error);
              setError('Erro ao carregar vídeo');

              initAttempts++;
              if (initAttempts < MAX_ATTEMPTS) {
                console.log(`Tentativa ${initAttempts} de reinicializar o player`);
                setTimeout(initPlayer, 1000);
              }
            }
          }
        });
      } catch (error) {
        console.error('Erro ao inicializar player:', error);
        initAttempts++;
        if (initAttempts < MAX_ATTEMPTS && mounted) {
          console.log(`Tentativa ${initAttempts} de reinicializar o player`);
          setTimeout(initPlayer, 1000);
        }
      }
    };

    // Pequeno delay antes de inicializar o player
    const initTimeout = setTimeout(initPlayer, 500);

    return () => {
      mounted = false;
      clearTimeout(initTimeout);
      if (player) {
        try {
          player.stopVideo();
          player.destroy();
        } catch (error) {
          console.error('Erro ao destruir player:', error);
        }
      }
    };
  }, [videos, selectedVideo]);

  // Controla o volume baseado nos eventos de áudio
  useEffect(() => {
    const handleAudioPlay = () => {
      console.log('Evento audioPlay recebido');
      if (player && isPlayerReady) {
        console.log('Baixando volume para 10%');
        fadeVolume(100, 10, 500);
      }
    };

    const handleAudioStop = () => {
      console.log('Evento audioStop recebido');
      if (player && isPlayerReady) {
        console.log('Aumentando volume para 100%');
        fadeVolume(10, 100, 500);
      }
    };

    const handleSpeechStart = () => {
      console.log('Evento speechStart recebido');
      if (player && isPlayerReady) {
        console.log('Baixando volume para 10%');
        fadeVolume(100, 10, 500);
      }
    };

    const handleSpeechEnd = () => {
      console.log('Evento speechEnd recebido');
      if (player && isPlayerReady) {
        console.log('Aumentando volume para 100%');
        fadeVolume(10, 100, 500);
      }
    };

    window.addEventListener('audioPlay', handleAudioPlay);
    window.addEventListener('audioStop', handleAudioStop);
    window.addEventListener('speechStart', handleSpeechStart);
    window.addEventListener('speechEnd', handleSpeechEnd);

    return () => {
      window.removeEventListener('audioPlay', handleAudioPlay);
      window.removeEventListener('audioStop', handleAudioStop);
      window.removeEventListener('speechStart', handleSpeechStart);
      window.removeEventListener('speechEnd', handleSpeechEnd);
    };
  }, [player, isPlayerReady]);

  // Atualiza o vídeo quando necessário
  useEffect(() => {
    if (!player || !isPlayerReady) return;

    const videoToPlay = selectedVideo || videos[0];
    if (!videoToPlay) return;

    const videoId = extractVideoId(videoToPlay.url);
    if (videoId && videoId !== currentVideoId) {
      console.log('Carregando novo vídeo:', videoId);
      try {
        // Primeiro para qualquer reprodução atual
        player.stopVideo();

        // Pequeno delay para garantir que o vídeo parou
        setTimeout(() => {
          if (!player || !isPlayerReady) return;

          setCurrentVideoId(videoId);

          // Carrega o vídeo pausado
          player.cueVideoById({
            videoId,
            startSeconds: 0,
            suggestedQuality: 'large'
          });

          // Se deveria estar tocando, inicia a reprodução
          if (isPlaying) {
            setTimeout(() => {
              if (!player || !isPlayerReady) return;
              player.playVideo();
            }, 100);
          }
        }, 100);
      } catch (error) {
        console.error('Erro ao carregar vídeo:', error);
        setError('Erro ao carregar vídeo');
      }
    }
  }, [selectedVideo, videos, player, isPlayerReady, isPlaying, currentVideoId]);

  // Limpa o player ao desmontar
  useEffect(() => {
    return () => {
      if (player) {
        try {
          // Garante que o vídeo está pausado antes de destruir
          player.pauseVideo();
          // Pequeno delay antes de destruir
          setTimeout(() => {
            try {
              player.destroy();
            } catch (error) {
              console.error('Erro ao destruir player:', error);
            }
          }, 100);
        } catch (error) {
          console.error('Erro ao pausar vídeo:', error);
        }
      }
    };
  }, [player]);

  // Monitora mudanças de rota/navegação
  useEffect(() => {
    const handleBeforeUnload = () => {
      if (player) {
        try {
          player.pauseVideo();
        } catch (error) {
          console.error('Erro ao pausar vídeo antes de sair:', error);
        }
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [player]);

  const fadeVolume = (start: number, end: number, duration: number) => {
    if (!player || !isPlayerReady) {
      console.log('Player não está pronto para ajuste de volume');
      return;
    }

    console.log(`Iniciando fade de volume: ${start}% -> ${end}%`);
    const steps = 20;
    const stepValue = (end - start) / steps;
    const stepDuration = duration / steps;
    let currentStep = 0;

    const fadeInterval = setInterval(() => {
      if (!player || !isPlayerReady) {
        console.log('Player não disponível durante fade, cancelando');
        clearInterval(fadeInterval);
        return;
      }

      currentStep++;
      const newVolume = Math.round(start + (stepValue * currentStep));

      try {
        console.log(`Ajustando volume para ${newVolume}%`);
        player.setVolume(newVolume);
      } catch (error) {
        console.error('Erro ao ajustar volume:', error);
        clearInterval(fadeInterval);
      }

      if (currentStep >= steps) {
        console.log('Fade de volume concluído');
        clearInterval(fadeInterval);
      }
    }, stepDuration);
  };

  const extractVideoId = (url: string): string | null => {
    if (!url) return null;

    try {
      let videoId = '';

      if (url.includes('youtube.com/embed/')) {
        videoId = url.split('embed/')[1]?.split('?')[0];
      } else if (url.includes('youtube.com/watch')) {
        videoId = url.split('v=')[1]?.split('&')[0];
      } else if (url.includes('youtu.be/')) {
        videoId = url.split('youtu.be/')[1]?.split('?')[0];
      }

      return videoId || null;
    } catch (error) {
      console.error('Erro ao extrair videoId:', error);
      return null;
    }
  };

  const loadVideos = async () => {
    try {
      const data = await videoService.getRecentVideos();
      setVideos(data);
    } catch (error) {
      console.error('Erro ao carregar vídeos:', error);
    }
  };

  const handleVideoSelect = (video: VideoData) => {
    setSelectedVideo(video);
    setIsSidebarOpen(false);
  };

  const handleAddVideo = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    setError('');
    setIsLoading(true);

    if (!newVideoUrl.trim()) {
      setError('Por favor, insira uma URL válida');
      setIsLoading(false);
      return;
    }

    try {
      const embedUrl = getEmbedUrl(newVideoUrl);
      const videoTitle = await getVideoTitle(newVideoUrl);

      const newVideo = await videoService.addVideo(embedUrl, videoTitle);
      setVideos(prev => [newVideo, ...prev]);
      setNewVideoUrl('');
      setSelectedVideo(newVideo);
    } catch (error: any) {
      console.error('Erro detalhado:', error);
      setError(
        error.message || 
        error.error_description || 
        'Erro ao adicionar vídeo. Verifique se a URL é válida.'
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async (videoId: string) => {
    try {
      await videoService.deleteVideo(videoId);
      setVideos(prev => prev.filter(video => video.id !== videoId));
    } catch (error) {
      console.error('Erro ao deletar vídeo:', error);
    }
  };

  const getEmbedUrl = (url: string) => {
    try {
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com') 
          ? url.split('v=')[1]?.split('&')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];

        return `https://www.youtube.com/embed/${videoId}`;
      }
      if (url.includes('vimeo.com')) {
        const videoId = url.split('vimeo.com/')[1]?.split('?')[0];
        return `https://player.vimeo.com/video/${videoId}`;
      }
      return url;
    } catch (error) {
      console.error('Erro ao processar URL:', error);
      return url;
    }
  };

  const getVideoTitle = async (url: string): Promise<string> => {
    try {
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com')
          ? url.split('v=')[1]?.split('&')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];

        const response = await fetch(
          `https://www.googleapis.com/youtube/v3/videos?id=${videoId}&key=${process.env.REACT_APP_YOUTUBE_API_KEY}&part=snippet`
        );
        const data = await response.json();

        if (data.error) {
          console.error('Erro da API do YouTube:', data.error);
          return 'Vídeo do YouTube';
        }

        return data.items[0]?.snippet?.title || 'Vídeo do YouTube';
      }

      if (url.includes('vimeo.com')) {
        const videoId = url.split('vimeo.com/')[1]?.split('?')[0];
        const response = await fetch(`https://vimeo.com/api/v2/video/${videoId}.json`);
        const data = await response.json();
        return data[0]?.title || 'Vídeo do Vimeo';
      }

      return 'Vídeo';
    } catch (error) {
      console.error('Erro ao obter título do vídeo:', error);
      return 'Vídeo';
    }
  };

  const handleStateChange = (event: any) => {
    if (event.data === 0) { // vídeo terminou
      setIsPlaying(false);
      window.dispatchEvent(new Event('externalMediaStop'));

      if (pendingVideoId) {
        const pendingVideo = videos.find(v => v.id === pendingVideoId);
        if (pendingVideo) {
          setSelectedVideo(pendingVideo);
          setIsPlaying(true);
        }
      }

      onEnded();
    } else if (event.data === 1) { // vídeo começou a tocar
      setIsPlaying(true);
      window.dispatchEvent(new Event('externalMediaPlay'));
    } else if (event.data === 2) { // vídeo foi pausado
      setIsPlaying(false);
      window.dispatchEvent(new Event('externalMediaStop'));
    }
  };

  return (
    <div className="bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg p-3">
      {/* Formulário de busca */}
      <form onSubmit={handleAddVideo} className="mb-3">
        <div className="flex flex-col gap-2">
          <input
            type="url"
            value={newVideoUrl}
            onChange={(e) => setNewVideoUrl(e.target.value)}
            placeholder="Cole a URL do YouTube ou Vimeo aqui"
            className="w-full bg-[#2d2d2d] border border-[#404040] text-gray-200 rounded px-3 py-2 focus:border-[#e1aa1e] focus:outline-none"
            disabled={isLoading}
          />
          {error && <p className="text-red-400 text-sm">{error}</p>}
        </div>
      </form>

      {/* Botões de ação */}
      <div className="flex gap-2 mb-3">
        <button
          type="button"
          onClick={() => handleAddVideo()}
          className={`flex-1 ${
            isLoading 
              ? 'bg-[#e1aa1e]/50 cursor-not-allowed' 
              : 'bg-[#e1aa1e] hover:bg-[#e1aa1e]/80'
          } text-gray-900 px-3 py-1.5 rounded transition-colors flex items-center justify-center gap-2`}
          disabled={isLoading}
        >
          <svg
            className="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 4v16m8-8H4"
            />
          </svg>
          {isLoading ? 'Adicionando...' : 'Buscar Vídeo'}
        </button>

        <button
          type="button"
          onClick={() => setIsSidebarOpen(true)}
          className="flex-1 bg-[#2d2d2d] hover:bg-[#404040] border border-[#404040] text-[#e1aa1e] px-3 py-1.5 rounded flex items-center justify-center gap-2 transition-colors"
        >
          <svg
            className="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
          Últimos Vídeos
        </button>
      </div>

      {/* Área do vídeo */}
      <div>
        {(selectedVideo || videos[0]) ? (
          <div className="rounded-lg overflow-hidden border border-[#404040]">
            <div className="relative pt-[56.25%]">
              <div id="player-container" className="absolute top-0 left-0 w-full h-full">
                <div id="youtube-player" className="w-full h-full"></div>
              </div>
            </div>
          </div>
        ) : (
          <div className="text-center p-6 text-gray-400 bg-[#2d2d2d] rounded-lg border border-[#404040]">
            Nenhum vídeo disponível. Adicione um vídeo para começar.
          </div>
        )}
      </div>

      {/* Menu lateral */}
      <VideoSidebar
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        videos={videos}
        onVideoSelect={handleVideoSelect}
      />
    </div>
  );
};

export default VideoPlayer;
