import React, { useState, useEffect } from 'react';
import { User, UserListItem } from '../types/auth';
import { authService } from '../lib/auth';

interface UserSettingsProps {
  user: User;
  onClose: () => void;
  onSuccess: () => void;
}

const UserSettings: React.FC<UserSettingsProps> = ({ user, onClose, onSuccess }) => {
  const [selectedUser, setSelectedUser] = useState<string>(user.username);
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [adminPassword, setAdminPassword] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [users, setUsers] = useState<UserListItem[]>([]);

  // Carrega a lista de usuários
  useEffect(() => {
    const loadUsers = async () => {
      try {
        console.log('Carregando lista de usuários...');
        const userList = await authService.listUsers();
        console.log('Usuários carregados:', userList);
        setUsers(userList);
      } catch (err) {
        console.error('Erro ao carregar usuários:', err);
      }
    };

    loadUsers();
  }, []);

  // Para debug - mostra quando a lista de usuários muda
  useEffect(() => {
    console.log('Lista de usuários atualizada:', users);
  }, [users]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    setLoading(true);

    try {
      if (newPassword && newPassword !== confirmPassword) {
        setError('As senhas não coincidem');
        return;
      }

      if (newPassword && newPassword.length < 6) {
        setError('A nova senha deve ter pelo menos 6 caracteres');
        return;
      }

      if (!adminPassword) {
        setError('A senha do administrador é obrigatória');
        return;
      }

      // Atualiza o nome de usuário se fornecido
      if (newUsername) {
        const usernameUpdated = await authService.updateUsername(
          selectedUser,
          newUsername,
          adminPassword
        );

        if (!usernameUpdated) {
          setError('Erro ao atualizar nome de usuário');
          return;
        }
      }

      // Atualiza a senha se fornecida
      if (newPassword) {
        try {
          console.log('Tentando atualizar senha para:', selectedUser);
          const passwordUpdated = await authService.changePassword(
            selectedUser,
            adminPassword,
            newPassword
          );

          console.log('Resultado da atualização de senha:', passwordUpdated);

          if (!passwordUpdated) {
            setError('Erro ao atualizar senha - senha do administrador incorreta');
            return;
          }
        } catch (err) {
          console.error('Erro ao atualizar senha:', err);
          setError('Erro ao atualizar senha. Verifique sua senha de administrador.');
          return;
        }
      }

      setSuccess('Configurações atualizadas com sucesso!');
      setTimeout(() => {
        onSuccess();
        onClose();
      }, 2000);

    } catch (err) {
      setError(err instanceof Error ? err.message : 'Erro ao atualizar configurações');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
      <div className="bg-[#2d2d2d] p-6 rounded-lg shadow-lg w-[480px] border border-[#404040]">
        <h2 className="text-xl font-semibold mb-4 text-[#e1aa1e]">Gerenciar Usuários</h2>
        
        {error && (
          <div className="mb-4 p-3 bg-red-500/10 border border-red-500/20 rounded text-red-400 text-sm">
            {error}
          </div>
        )}

        {success && (
          <div className="mb-4 p-3 bg-green-500/10 border border-green-500/20 rounded text-green-400 text-sm">
            {success}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="space-y-4">
            <div>
              <label className="block text-gray-300 text-sm font-medium mb-2">
                Selecionar Usuário
              </label>
              <select
                value={selectedUser}
                onChange={(e) => setSelectedUser(e.target.value)}
                className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300"
              >
                {users.map(user => (
                  <option key={user.id} value={user.username}>
                    {user.username} [{user.full_name}]
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-gray-300 text-sm font-medium mb-2">
                Novo Nome de Usuário
              </label>
              <input
                type="text"
                value={newUsername}
                onChange={(e) => setNewUsername(e.target.value)}
                className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300"
                placeholder="Deixe em branco para manter o atual"
              />
            </div>

            <div>
              <label className="block text-gray-300 text-sm font-medium mb-2">
                Nova Senha
              </label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300"
                placeholder="Deixe em branco para manter a atual"
              />
            </div>

            <div>
              <label className="block text-gray-300 text-sm font-medium mb-2">
                Confirmar Nova Senha
              </label>
              <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300"
                disabled={!newPassword}
              />
            </div>

            <div>
              <label className="block text-gray-300 text-sm font-medium mb-2">
                Sua Senha (Admin)
              </label>
              <input
                type="password"
                value={adminPassword}
                onChange={(e) => setAdminPassword(e.target.value)}
                className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300"
                required
              />
            </div>
          </div>

          <div className="flex justify-end gap-3 mt-6">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 bg-[#404040] text-gray-300 rounded hover:bg-[#505050]"
            >
              Cancelar
            </button>
            <button
              type="submit"
              disabled={loading || !!success}
              className={`px-4 py-2 bg-[#e1aa1e] text-gray-900 rounded font-medium 
                ${(loading || success) ? 'opacity-50 cursor-not-allowed' : 'hover:bg-[#e1aa1e]/80'}`}
            >
              {loading ? 'Salvando...' : 'Salvar Alterações'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UserSettings;
