import React, { useState, useEffect } from 'react';
import { authService } from '../lib/auth';

interface LoginProps {
  onLoginSuccess: () => void;
}

const Login: React.FC<LoginProps> = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [isExiting, setIsExiting] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    if (isBlocked) {
      setError(`Aguarde ${blockTimeLeft} segundos para tentar novamente`);
      return;
    }

    if (!username.trim() || !password.trim()) {
      setError('Por favor, preencha todos os campos');
      return;
    }

    try {
      setLoading(true);
      const user = await authService.signIn(username, password);
      if (user) {
        setIsExiting(true);
        setTimeout(() => {
          resetLoginAttempts();
          onLoginSuccess();
        }, 500);
      } else {
        handleFailedLogin();
      }
    } catch (err) {
      console.error('Erro no login:', err);
      handleFailedLogin();
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-[#1e1e1e] to-[#141414] px-4">
      <div className="w-full max-w-md">
        <div className={`bg-[#2d2d2d]/90 backdrop-blur-sm p-8 rounded-lg shadow-2xl border border-[#404040] ${isExiting ? 'login-exit' : 'login-enter'}`}>
          <div className="text-center mb-8 animate-slideDown">
            <div className="w-20 h-20 mx-auto mb-4 rounded-full bg-[#e1aa1e]/10 flex items-center justify-center">
              <span className="text-[#e1aa1e] text-3xl font-bold">IF</span>
            </div>
            <h2 className="text-2xl font-bold text-[#e1aa1e]">Império Fitness</h2>
            <p className="text-gray-400 text-sm mt-1">Área Restrita</p>
          </div>

          {error && (
            <div className="mb-4 p-3 bg-red-500/10 border border-red-500/20 rounded text-red-400 text-sm">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-gray-300 text-sm font-medium mb-2">
                Usuário
              </label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300 focus:outline-none focus:border-[#e1aa1e] transition-colors"
                required
              />
            </div>

            <div>
              <label className="block text-gray-300 text-sm font-medium mb-2">
                Senha
              </label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300 focus:outline-none focus:border-[#e1aa1e] transition-colors"
                required
              />
            </div>

            <button
              type="submit"
              disabled={loading}
              className={`w-full py-2 px-4 bg-[#e1aa1e] text-gray-900 rounded-md font-medium 
                ${loading ? 'opacity-50 cursor-not-allowed' : 'hover:bg-[#e1aa1e]/80'} 
                transition-colors`}
            >
              {loading ? 'Entrando...' : 'Entrar'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
