import React, { useState, useEffect } from 'react';
import { videoService, VideoData } from '../lib/supabase';
import VideoSidebar from './VideoSidebar';

interface VideoPlayerProps {
  videoId?: string;
  onEnded: () => void;
  isPlaying: boolean;
  setIsPlaying: (playing: boolean) => void;
  autoplay?: boolean;
  pendingVideoId?: string;
}

const VideoPlayer: React.FC<VideoPlayerProps> = ({ 
  videoId,
  onEnded, 
  isPlaying, 
  setIsPlaying,
  autoplay = false,
  pendingVideoId 
}) => {
  const [videos, setVideos] = useState<VideoData[]>([]);
  const [newVideoUrl, setNewVideoUrl] = useState('');
  const [error, setError] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [selectedVideo, setSelectedVideo] = useState<VideoData | null>(null);
  const [player, setPlayer] = useState<any>(null);
  const [isPlayerReady, setIsPlayerReady] = useState(false);

  // Carrega vídeos quando o componente monta
  useEffect(() => {
    loadVideos();
  }, []);

  // Inicializa o player quando o componente monta
  useEffect(() => {
    let mounted = true;

    const setup = async () => {
      try {
        await initializePlayer();
      } catch (error) {
        console.error('Erro ao configurar player:', error);
      }
    };

    setup();

    return () => {
      mounted = false;
      if (player) {
        try {
          player.destroy();
        } catch (error) {
          console.error('Erro ao destruir player:', error);
        }
      }
    };
  }, []);

  // Controla o volume baseado nos eventos de áudio
  useEffect(() => {
    if (!player || !isPlayerReady) return;

    const handleAudioPlay = () => fadeVolume(100, 10, 500);
    const handleAudioStop = () => fadeVolume(10, 100, 500);
    const handleSpeechStart = () => fadeVolume(100, 10, 500);
    const handleSpeechEnd = () => fadeVolume(10, 100, 500);

    window.addEventListener('audioPlay', handleAudioPlay);
    window.addEventListener('audioStop', handleAudioStop);
    window.addEventListener('speechStart', handleSpeechStart);
    window.addEventListener('speechEnd', handleSpeechEnd);

    return () => {
      window.removeEventListener('audioPlay', handleAudioPlay);
      window.removeEventListener('audioStop', handleAudioStop);
      window.removeEventListener('speechStart', handleSpeechStart);
      window.removeEventListener('speechEnd', handleSpeechEnd);
    };
  }, [player, isPlayerReady]);

  // Atualiza o player quando o vídeo muda
  useEffect(() => {
    if (!player || !isPlayerReady) return;

    const videoUrl = selectedVideo?.url || videos[0]?.url;
    if (!videoUrl) return;

    try {
      const videoId = extractVideoId(videoUrl);
      if (videoId) {
        player.loadVideoById({
          videoId,
          startSeconds: 0,
          suggestedQuality: 'large'
        });
      }
    } catch (error) {
      console.error('Erro ao atualizar vídeo:', error);
    }
  }, [selectedVideo?.url, videos[0]?.url, player, isPlayerReady]);

  const extractVideoId = (url: string): string | null => {
    if (!url) return null;

    try {
      let videoId = '';

      if (url.includes('youtube.com/embed/')) {
        videoId = url.split('embed/')[1]?.split('?')[0];
      } else if (url.includes('youtube.com/watch')) {
        videoId = url.split('v=')[1]?.split('&')[0];
      } else if (url.includes('youtu.be/')) {
        videoId = url.split('youtu.be/')[1]?.split('?')[0];
      }

      return videoId || null;
    } catch (error) {
      console.error('Erro ao extrair videoId:', error);
      return null;
    }
  };

  const fadeVolume = (start: number, end: number, duration: number) => {
    if (!player || !isPlayerReady) return;

    const steps = 20;
    const stepValue = (end - start) / steps;
    const stepDuration = duration / steps;
    let currentStep = 0;

    const fadeInterval = setInterval(() => {
      if (!player || !isPlayerReady) {
        clearInterval(fadeInterval);
        return;
      }

      currentStep++;
      const newVolume = Math.round(start + (stepValue * currentStep));

      try {
        player.setVolume(newVolume);
      } catch (error) {
        console.error('Erro ao ajustar volume:', error);
        clearInterval(fadeInterval);
      }

      if (currentStep >= steps) {
        clearInterval(fadeInterval);
      }
    }, stepDuration);
  };

  const initializePlayer = async () => {
    try {
      await new Promise<void>((resolve) => {
        if ((window as any).YT) {
          resolve();
          return;
        }

        const tag = document.createElement('script');
        tag.src = 'https://www.youtube.com/iframe_api';
        tag.async = true;
        const firstScriptTag = document.getElementsByTagName('script')[0];
        firstScriptTag.parentNode?.insertBefore(tag, firstScriptTag);

        (window as any).onYouTubeIframeAPIReady = () => {
          resolve();
        };
      });

      // Aguarda um pequeno intervalo para garantir que o iframe existe
      await new Promise(resolve => setTimeout(resolve, 100));

      const iframe = document.getElementById('youtube-player');
      if (!iframe) {
        console.error('Iframe não encontrado');
        return;
      }

      const newPlayer = new (window as any).YT.Player('youtube-player', {
        events: {
          onReady: () => {
            console.log('Player pronto');
            setIsPlayerReady(true);
            setPlayer(newPlayer);
          },
          onStateChange: (event: any) => {
            if (event.data === (window as any).YT.PlayerState.ENDED) {
              setIsPlaying(false);
              onEnded();
            } else if (event.data === (window as any).YT.PlayerState.PLAYING) {
              setIsPlaying(true);
            } else if (event.data === (window as any).YT.PlayerState.PAUSED) {
              setIsPlaying(false);
            }
          },
          onError: (error: any) => {
            console.error('Erro no player:', error);
            setError('Erro ao carregar vídeo');
          }
        },
        playerVars: {
          enablejsapi: 1,
          origin: window.location.origin,
          widget_referrer: window.location.origin,
          playsinline: 1,
          controls: 1,
          rel: 0,
          modestbranding: 1,
          autoplay: 0
        }
      });
    } catch (error) {
      console.error('Erro ao inicializar player:', error);
    }
  };

  const loadVideos = async () => {
    try {
      const data = await videoService.getRecentVideos();
      setVideos(data);
    } catch (error) {
      console.error('Erro ao carregar vídeos:', error);
    }
  };

  const handleVideoSelect = (video: VideoData) => {
    setSelectedVideo(video);
    setIsSidebarOpen(false);
  };

  const handleAddVideo = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    setError('');
    setIsLoading(true);

    if (!newVideoUrl.trim()) {
      setError('Por favor, insira uma URL válida');
      setIsLoading(false);
      return;
    }

    try {
      const embedUrl = getEmbedUrl(newVideoUrl);
      const videoTitle = await getVideoTitle(newVideoUrl);

      const newVideo = await videoService.addVideo(embedUrl, videoTitle);
      setVideos(prev => [newVideo, ...prev]);
      setNewVideoUrl('');
      setSelectedVideo(newVideo);
    } catch (error: any) {
      console.error('Erro detalhado:', error);
      setError(
        error.message || 
        error.error_description || 
        'Erro ao adicionar vídeo. Verifique se a URL é válida.'
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async (videoId: string) => {
    try {
      await videoService.deleteVideo(videoId);
      setVideos(prev => prev.filter(video => video.id !== videoId));
    } catch (error) {
      console.error('Erro ao deletar vídeo:', error);
    }
  };

  const getEmbedUrl = (url: string) => {
    try {
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com') 
          ? url.split('v=')[1]?.split('&')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];

        return `https://www.youtube.com/embed/${videoId}`;
      }
      if (url.includes('vimeo.com')) {
        const videoId = url.split('vimeo.com/')[1]?.split('?')[0];
        return `https://player.vimeo.com/video/${videoId}`;
      }
      return url;
    } catch (error) {
      console.error('Erro ao processar URL:', error);
      return url;
    }
  };

  const getVideoTitle = async (url: string): Promise<string> => {
    try {
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com')
          ? url.split('v=')[1]?.split('&')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];

        const response = await fetch(
          `https://www.googleapis.com/youtube/v3/videos?id=${videoId}&key=${process.env.REACT_APP_YOUTUBE_API_KEY}&part=snippet`
        );
        const data = await response.json();

        if (data.error) {
          console.error('Erro da API do YouTube:', data.error);
          return 'Vídeo do YouTube';
        }

        return data.items[0]?.snippet?.title || 'Vídeo do YouTube';
      }

      if (url.includes('vimeo.com')) {
        const videoId = url.split('vimeo.com/')[1]?.split('?')[0];
        const response = await fetch(`https://vimeo.com/api/v2/video/${videoId}.json`);
        const data = await response.json();
        return data[0]?.title || 'Vídeo do Vimeo';
      }

      return 'Vídeo';
    } catch (error) {
      console.error('Erro ao obter título do vídeo:', error);
      return 'Vídeo';
    }
  };

  const handleStateChange = (event: any) => {
    if (event.data === 0) { // vídeo terminou
      setIsPlaying(false);
      window.dispatchEvent(new Event('externalMediaStop'));

      if (pendingVideoId) {
        const pendingVideo = videos.find(v => v.id === pendingVideoId);
        if (pendingVideo) {
          setSelectedVideo(pendingVideo);
          setIsPlaying(true);
        }
      }

      onEnded();
    } else if (event.data === 1) { // vídeo começou a tocar
      setIsPlaying(true);
      window.dispatchEvent(new Event('externalMediaPlay'));
    } else if (event.data === 2) { // vídeo foi pausado
      setIsPlaying(false);
      window.dispatchEvent(new Event('externalMediaStop'));
    }
  };

  return (
    <div className="bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg p-3">
      {/* Formulário de busca */}
      <form onSubmit={handleAddVideo} className="mb-3">
        <div className="flex flex-col gap-2">
          <input
            type="url"
            value={newVideoUrl}
            onChange={(e) => setNewVideoUrl(e.target.value)}
            placeholder="Cole a URL do YouTube ou Vimeo aqui"
            className="w-full bg-[#2d2d2d] border border-[#404040] text-gray-200 rounded px-3 py-2 focus:border-[#e1aa1e] focus:outline-none"
            disabled={isLoading}
          />
          {error && <p className="text-red-400 text-sm">{error}</p>}
        </div>
      </form>

      {/* Botões de ação */}
      <div className="flex gap-2 mb-3">
        <button
          type="button"
          onClick={() => handleAddVideo()}
          className={`flex-1 ${
            isLoading 
              ? 'bg-[#e1aa1e]/50 cursor-not-allowed' 
              : 'bg-[#e1aa1e] hover:bg-[#e1aa1e]/80'
          } text-gray-900 px-3 py-1.5 rounded transition-colors flex items-center justify-center gap-2`}
          disabled={isLoading}
        >
          <svg
            className="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 4v16m8-8H4"
            />
          </svg>
          {isLoading ? 'Adicionando...' : 'Buscar Vídeo'}
        </button>

        <button
          type="button"
          onClick={() => setIsSidebarOpen(true)}
          className="flex-1 bg-[#2d2d2d] hover:bg-[#404040] border border-[#404040] text-[#e1aa1e] px-3 py-1.5 rounded flex items-center justify-center gap-2 transition-colors"
        >
          <svg
            className="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
          Últimos Vídeos
        </button>
      </div>

      {/* Área do vídeo */}
      <div>
        {(selectedVideo || videos[0]) ? (
          <div className="rounded-lg overflow-hidden border border-[#404040]">
            <div className="relative pt-[56.25%]">
              <iframe
                id="youtube-player"
                src={`${selectedVideo?.url || videos[0]?.url}?enablejsapi=1&origin=${encodeURIComponent(window.location.origin)}&playsinline=1&widget_referrer=${encodeURIComponent(window.location.origin)}&controls=1&rel=0&modestbranding=1`}
                className="absolute top-0 left-0 w-full h-full"
                allowFullScreen
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                title={`Video ${selectedVideo?.id || videos[0]?.id}`}
              />
            </div>
          </div>
        ) : (
          <div className="text-center p-6 text-gray-400 bg-[#2d2d2d] rounded-lg border border-[#404040]">
            Nenhum vídeo disponível. Adicione um vídeo para começar.
          </div>
        )}
      </div>

      {/* Menu lateral */}
      <VideoSidebar
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        videos={videos}
        onVideoSelect={handleVideoSelect}
      />
    </div>
  );
};

export default VideoPlayer;
