import React, { useState, useEffect, useRef } from 'react';
import { audioService, AudioData } from '../lib/supabase';
import { supabase } from '../lib/supabase';
import '../styles/animations.css';
import { motion, AnimatePresence } from 'framer-motion';

interface AudioTimer {
  interval: number;
  timeLeft: number;
  intervalId: NodeJS.Timeout;
}

interface AudioPlayerProps {
  onEnded: () => void;
  isPlaying: boolean;
  setIsPlaying: (playing: boolean) => void;
}

const AudioPlayer: React.FC<AudioPlayerProps> = ({ onEnded, isPlaying, setIsPlaying }) => {
  const [audios, setAudios] = useState<AudioData[]>([]);
  const [currentAudio, setCurrentAudio] = useState<AudioData | null>(null);
  const [progress, setProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [audioTimers, setAudioTimers] = useState<Record<string, AudioTimer>>({});
  const timersRef = useRef<Record<string, NodeJS.Timeout>>({});
  const [timerAudioId, setTimerAudioId] = useState<string | null>(null);
  const [pendingAudioId, setPendingAudioId] = useState<string | null>(null);
  const [pendingAudios, setPendingAudios] = useState<string[]>([]);
  const playAttemptRef = useRef<NodeJS.Timeout | null>(null);
  const [editingAudioId, setEditingAudioId] = useState<string | null>(null);
  const [editingTitle, setEditingTitle] = useState('');
  const [showConfirmDialog, setShowConfirmDialog] = useState<string | null>(null);
  const [showTimerDialog, setShowTimerDialog] = useState<string | null>(null);
  const [popupPosition, setPopupPosition] = useState<{ x: number; y: number } | null>(null);
  const [confirmPosition, setConfirmPosition] = useState<{ x: number; y: number } | null>(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const [timerInputValue, setTimerInputValue] = useState('');
  const [preferences, setPreferences] = useState<Record<string, {
    auto_repeat: boolean;
    repeat_interval: number;
    last_played_at?: string;
    play_count: number;
  }>>({});

  useEffect(() => {
    loadAudios();
  }, []);

  useEffect(() => {
    const playAudio = async () => {
      if (!audioRef.current) return;

      try {
        console.log('Tentando reproduzir áudio...');
        await audioRef.current.play();
        console.log('Áudio iniciado com sucesso');
        window.dispatchEvent(new Event('audioPlay'));
      } catch (error: unknown) {
        console.error('Erro ao reproduzir áudio:', error);
        if (error instanceof Error && error.name === 'AbortError') {
          // Limpa qualquer tentativa pendente
          if (playAttemptRef.current) {
            clearTimeout(playAttemptRef.current);
          }
          
          // Tenta novamente após um delay
          playAttemptRef.current = setTimeout(async () => {
            try {
              console.log('Tentando reproduzir novamente...');
              await audioRef.current?.play();
              console.log('Áudio iniciado na segunda tentativa');
              window.dispatchEvent(new Event('audioPlay'));
            } catch (retryError) {
              console.error('Erro na segunda tentativa:', retryError);
              setIsPlaying(false);
            }
          }, 500);
        } else {
          setIsPlaying(false);
        }
      }
    };

    if (audioRef.current) {
      if (isPlaying) {
        // Limpa qualquer tentativa pendente antes de iniciar uma nova
        if (playAttemptRef.current) {
          clearTimeout(playAttemptRef.current);
          playAttemptRef.current = null;
        }
        
        // Adiciona um pequeno delay antes de tentar reproduzir
        playAttemptRef.current = setTimeout(() => {
          playAudio();
        }, 100);
      } else {
        // Limpa qualquer tentativa pendente
        if (playAttemptRef.current) {
          clearTimeout(playAttemptRef.current);
          playAttemptRef.current = null;
        }
        audioRef.current.pause();
        window.dispatchEvent(new Event('audioStop'));
      }
    }

    // Cleanup
    return () => {
      if (playAttemptRef.current) {
        clearTimeout(playAttemptRef.current);
        playAttemptRef.current = null;
      }
    };
  }, [isPlaying]);

  useEffect(() => {
    return () => {
      Object.values(timersRef.current).forEach(timer => {
        clearInterval(timer);
      });
    };
  }, []);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.onended = () => {
        console.log('Áudio terminou (evento nativo)');
        handleAudioEnd();
      };

      audioRef.current.onloadeddata = () => {
        if (isPlaying) {
          audioRef.current?.play().catch(error => {
            console.error('Erro ao reproduzir áudio após carregamento:', error);
            setIsPlaying(false);
          });
        }
      };
    }
  }, [audioRef.current]);

  useEffect(() => {
    Object.entries(audioTimers).forEach(([audioId, timer]) => {
      if (timer.timeLeft === 0) {
        setAudioTimers(prev => ({
          ...prev,
          [audioId]: {
            ...timer,
            timeLeft: timer.interval
          }
        }));

        if (!isPlaying) {
          const audio = audios.find(a => a.id === audioId);
          if (audio && audioRef.current) {
            const playWithDelay = async () => {
              try {
                await new Promise(resolve => setTimeout(resolve, 500));
                setCurrentAudio(audio);
                
                if (audioRef.current) {
                  audioRef.current.currentTime = 0;
                  console.log('Tentando reproduzir áudio automático:', audio.title);
                  await audioRef.current.play();
                  setIsPlaying(true);
                  console.log('Áudio automático iniciado com sucesso');
                }
              } catch (error) {
                console.error('Erro ao reproduzir áudio automático:', error);
                if (error instanceof Error && error.name === 'AbortError') {
                  console.log('Tentando novamente em 1 segundo...');
                  setTimeout(async () => {
                    try {
                      if (audioRef.current) {
                        await audioRef.current.play();
                        setIsPlaying(true);
                        console.log('Áudio automático iniciado na segunda tentativa');
                      }
                    } catch (retryError) {
                      console.error('Falha na segunda tentativa:', retryError);
                      console.log('Adicionando à fila de pendentes após falha');
                      setPendingAudios(prev => 
                        prev.includes(audioId) ? prev : [...prev, audioId]
                      );
                    }
                  }, 1000);
                }
              }
            };

            playWithDelay();
          }
        } else {
          console.log('Adicionando áudio à fila de pendentes:', audioId);
          setPendingAudios(prev => {
            if (prev.includes(audioId)) return prev;
            console.log('Fila atualizada:', [...prev, audioId]);
            return [...prev, audioId];
          });
        }
      }
    });
  }, [audioTimers, audios, isPlaying]);

  const loadAudios = async () => {
    try {
      const data = await audioService.getAudios();
      setAudios(data);
      
      // Carrega as preferências para cada áudio
      data.forEach(audio => {
        loadAudioPreferences(audio.id);
      });
    } catch (error) {
      console.error('Erro ao carregar áudios:', error);
    }
  };

  const loadAudioPreferences = async (audioId: string) => {
    try {
      const prefs = await audioService.loadPreferences(audioId);
      setPreferences(prev => ({
        ...prev,
        [audioId]: {
          auto_repeat: prefs.auto_repeat || false,
          repeat_interval: prefs.repeat_interval || 0,
          last_played_at: prefs.last_played_at,
          play_count: prefs.play_count || 0
        }
      }));

      // Se tiver repetição automática ativa, inicia o timer
      if (prefs.auto_repeat && prefs.repeat_interval) {
        startRepeatTimer(audioId, prefs.repeat_interval);
      }
    } catch (error) {
      console.error('Erro ao carregar preferências:', error);
    }
  };

  const handleTimeUpdate = () => {
    if (audioRef.current) {
      const progress = (audioRef.current.currentTime / audioRef.current.duration) * 100;
      setProgress(progress);
    }
  };

  const handleProgressClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (audioRef.current) {
      const bounds = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - bounds.left;
      const width = bounds.width;
      const percentage = x / width;
      audioRef.current.currentTime = audioRef.current.duration * percentage;
    }
  };

  const handleAudioEnd = async () => {
    console.log('Áudio terminou, verificando pendentes...');
    console.log('Fila de pendentes:', pendingAudios);
    
    if (playAttemptRef.current) {
      clearTimeout(playAttemptRef.current);
      playAttemptRef.current = null;
    }

    if (audioRef.current) {
      audioRef.current.currentTime = 0;
    }
    setProgress(0);

    // Adiciona um delay antes de verificar a fila de pendentes
    await new Promise(resolve => setTimeout(resolve, 300));

    if (pendingAudios.length > 0) {
      try {
        console.log('Existem áudios pendentes, reproduzindo próximo...');
        const nextAudioId = pendingAudios[0];
        const nextAudio = audios.find(a => a.id === nextAudioId);

        setPendingAudios(prev => prev.slice(1));

        if (nextAudio) {
          console.log('Iniciando reprodução do áudio pendente:', nextAudio.title);
          // Aumenta o delay antes de iniciar o próximo áudio
          await new Promise(resolve => setTimeout(resolve, 800));
          setCurrentAudio(nextAudio);
          setIsPlaying(true);
        }
      } catch (error) {
        console.error('Erro ao reproduzir próximo áudio:', error);
        setIsPlaying(false);
        onEnded();
      }
    } else {
      console.log('Não há áudios pendentes');
      setIsPlaying(false);
      onEnded();
    }

    window.dispatchEvent(new Event('audioStop'));
  };

  const togglePlay = async (audio: AudioData) => {
    if (currentAudio?.id === audio.id) {
      setIsPlaying(!isPlaying);
    } else {
      console.log('Mudando para novo áudio manualmente');
      setCurrentAudio(audio);
      if (audioRef.current) {
        audioRef.current.currentTime = 0;
      }
      setIsPlaying(true);
      // Não vamos mais limpar a fila de pendentes ao tocar manualmente
      // setPendingAudios([]);
    }

    if (!isPlaying) {
      try {
        await audioService.updatePreferences(audio.id, {
          last_played_at: new Date().toISOString(),
          play_count: (preferences[audio.id]?.play_count || 0) + 1
        });
      } catch (error) {
        console.error('Erro ao atualizar estatísticas de reprodução:', error);
      }
    }
  };

  const handleUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    try {
      console.log('Iniciando upload do arquivo:', file.name);
      
      // Upload do arquivo
      const fileExt = file.name.split('.').pop();
      const fileName = `${Math.random()}.${fileExt}`;
      const filePath = `audios/${fileName}`;

      console.log('Tentando fazer upload para:', filePath);

      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('media')
        .upload(filePath, file);

      if (uploadError) {
        console.error('Erro no upload:', uploadError);
        throw uploadError;
      }

      console.log('Upload bem sucedido:', uploadData);

      // Obtém a URL pública
      const { data: { publicUrl } } = supabase.storage
        .from('media')
        .getPublicUrl(filePath);

      console.log('URL pública gerada:', publicUrl);

      // Adiciona ao banco de dados
      const newAudio = await audioService.addAudio({
        title: file.name,
        url: publicUrl,
        is_auto_play: false
      });

      console.log('Áudio adicionado ao banco:', newAudio);

      // Recarrega todos os áudios
      await loadAudios();
      
      // Seleciona o novo áudio
      setCurrentAudio(newAudio);
      
      // Limpa o input
      event.target.value = '';

    } catch (error) {
      console.error('Erro detalhado no upload:', error);
    } finally {
      setIsUploading(false);
    }
  };

  const startRepeatTimer = async (audioId: string, minutes: number) => {
    setTimerAudioId(audioId);

    if (timersRef.current[audioId]) {
      clearInterval(timersRef.current[audioId]);
    }

    const seconds = minutes * 60;
    
    const intervalId = setInterval(() => {
      setAudioTimers(prev => {
        const currentTimer = prev[audioId];
        if (!currentTimer) return prev;

        const newTimeLeft = currentTimer.timeLeft - 1;
        return {
          ...prev,
          [audioId]: {
            ...currentTimer,
            timeLeft: newTimeLeft
          }
        };
      });
    }, 1000);

    timersRef.current[audioId] = intervalId;

    setAudioTimers(prev => ({
      ...prev,
      [audioId]: {
        interval: seconds,
        timeLeft: seconds,
        intervalId
      }
    }));

    // Salva as preferências no banco
    try {
      await audioService.updatePreferences(audioId, {
        auto_repeat: true,
        repeat_interval: minutes
      });
    } catch (error) {
      console.error('Erro ao salvar preferências:', error);
    }
  };

  const stopRepeatTimer = async (audioId: string) => {
    if (timersRef.current[audioId]) {
      clearInterval(timersRef.current[audioId]);
      delete timersRef.current[audioId];
    }

    setAudioTimers(prev => {
      const newTimers = { ...prev };
      delete newTimers[audioId];
      return newTimers;
    });

    // Salva as preferências no banco
    try {
      await audioService.updatePreferences(audioId, {
        auto_repeat: false,
        repeat_interval: 0
      });
    } catch (error) {
      console.error('Erro ao salvar preferências:', error);
    }
  };

  const handleDelete = async (audioId: string) => {
    try {
      await audioService.deleteAudio(audioId);
      if (currentAudio?.id === audioId) {
        setCurrentAudio(null);
        setIsPlaying(false);
      }
      await loadAudios();
    } catch (error) {
      console.error('Erro ao deletar áudio:', error);
    }
  };

  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Adicione esta função auxiliar para formatar o tempo do timer
  const formatTimeLeft = (seconds: number): string => {
    if (seconds < 60) {
      return `${Math.ceil(seconds)}s`;
    }
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.ceil(seconds % 60);
    return `${minutes}min ${remainingSeconds}s`;
  };

  // Modifique a função getNextAudioInfo para verificar os timers ativos
  const getNextAudioInfo = () => {
    // Primeiro, verifica se há algum áudio pendente na fila
    if (pendingAudios.length > 0) {
      const nextAudioId = pendingAudios[0];
      const nextAudio = audios.find(a => a.id === nextAudioId);
      const timer = audioTimers[nextAudioId];
      
      return { nextAudio, timer };
    }

    // Se não há áudios pendentes, procura o próximo áudio agendado
    let nextScheduledAudio = null;
    let shortestTime = Infinity;

    Object.entries(audioTimers).forEach(([audioId, timer]) => {
      if (timer.timeLeft < shortestTime) {
        const audio = audios.find(a => a.id === audioId);
        if (audio) {
          nextScheduledAudio = {
            nextAudio: audio,
            timer: timer
          };
          shortestTime = timer.timeLeft;
        }
      }
    });

    return nextScheduledAudio;
  };

  const handleUpdateTitle = async (audioId: string, newTitle: string) => {
    try {
      await audioService.updateAudio(audioId, { title: newTitle });
      // Atualiza o estado local dos áudios
      setAudios(prev => prev.map(audio => 
        audio.id === audioId ? { ...audio, title: newTitle } : audio
      ));
      // Se o áudio sendo editado é o atual, atualiza também o currentAudio
      if (currentAudio?.id === audioId) {
        setCurrentAudio(prev => prev ? { ...prev, title: newTitle } : null);
      }
      setEditingAudioId(null);
    } catch (error) {
      console.error('Erro ao atualizar título:', error);
    }
  };

  const ConfirmDialog = ({ 
    audioId, 
    onConfirm, 
    onCancel,
    buttonPosition
  }: { 
    audioId: string;
    onConfirm: () => void;
    onCancel: () => void;
    buttonPosition: { x: number; y: number } | null;
  }) => (
    <div 
      className="fixed inset-0 z-50"
      onClick={(e) => {
        if (e.target === e.currentTarget) onCancel();
      }}
    >
      <div 
        className="absolute bg-[#2d2d2d] p-4 rounded-lg border border-[#404040] shadow-lg w-64"
        style={{
          top: buttonPosition ? `${buttonPosition.y - 80}px` : '50%',
          left: buttonPosition ? `${buttonPosition.x - 100}px` : '50%',
          transform: isAnimating ? 'scale(0.9)' : 'scale(1)',
          opacity: isAnimating ? 0 : 1,
          transition: 'transform 0.2s ease-out, opacity 0.2s ease-out'
        }}
      >
        <h3 className="text-[#e1aa1e] font-medium text-sm mb-2">Desativar Repetição</h3>
        <p className="text-gray-300 text-xs mb-4">
          Deseja realmente desativar a repetição automática?
        </p>
        <div className="flex justify-end gap-2">
          <button
            onClick={onCancel}
            className="px-3 py-1.5 rounded bg-[#404040] text-xs text-gray-200 hover:bg-[#505050] transition-colors"
          >
            Cancelar
          </button>
          <button
            onClick={onConfirm}
            className="px-3 py-1.5 rounded bg-[#e1aa1e] text-xs text-gray-900 hover:bg-[#e1aa1e]/80 transition-colors"
          >
            Confirmar
          </button>
        </div>
      </div>
    </div>
  );

  const SetTimerDialog = ({ 
    audioId, 
    audioTitle,
    onConfirm, 
    onCancel,
    buttonPosition,
    inputValue,
    onInputChange
  }: { 
    audioId: string;
    audioTitle: string;
    onConfirm: (minutes: number) => void;
    onCancel: () => void;
    buttonPosition: { x: number; y: number } | null;
    inputValue: string;
    onInputChange: (value: string) => void;
  }) => {
    return (
      <div className="fixed inset-0 z-50" onClick={(e) => {
        if (e.target === e.currentTarget) onCancel();
      }}>
        <div 
          className="absolute bg-[#2d2d2d] p-4 rounded-lg border border-[#404040] shadow-lg w-64"
          style={{
            top: buttonPosition ? `${buttonPosition.y - 80}px` : '50%',
            left: buttonPosition ? `${buttonPosition.x - 100}px` : '50%',
            transform: isAnimating ? 'scale(0.9)' : 'scale(1)',
            opacity: isAnimating ? 0 : 1,
            transition: 'transform 0.2s ease-out, opacity 0.2s ease-out'
          }}
        >
          <h3 className="text-[#e1aa1e] font-medium text-sm mb-2">Repetir Áudio</h3>
          <p className="text-gray-300 text-xs mb-3 truncate">
            {audioTitle}
          </p>
          
          <div className="relative mb-4">
            <input
              type="number"
              min="1"
              value={inputValue}
              onChange={(e) => onInputChange(e.target.value)}
              placeholder="00"
              className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded text-gray-200 text-center focus:border-[#e1aa1e] focus:outline-none [appearance:textfield] [&::-webkit-outer-spin-button]:appearance-none [&::-webkit-inner-spin-button]:appearance-none pr-12"
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  const value = parseInt(inputValue);
                  if (value > 0) onConfirm(value);
                } else if (e.key === 'Escape') {
                  onCancel();
                }
              }}
              autoFocus
            />
            <span className="absolute right-3 top-1/2 -translate-y-1/2 text-xs text-gray-400">
              minutos
            </span>
          </div>

          <div className="flex justify-end gap-2">
            <button
              onClick={onCancel}
              className="px-3 py-1.5 rounded bg-[#404040] text-xs text-gray-200 hover:bg-[#505050] transition-colors"
            >
              Cancelar
            </button>
            <button
              onClick={() => {
                const value = parseInt(inputValue);
                if (value > 0) onConfirm(value);
              }}
              disabled={!inputValue || parseInt(inputValue) <= 0}
              className="px-3 py-1.5 rounded bg-[#e1aa1e] text-xs text-gray-900 hover:bg-[#e1aa1e]/80 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Ativar
            </button>
          </div>
        </div>
      </div>
    );
  };

  const handleShowTimerDialog = (e: React.MouseEvent, audioId: string) => {
    const rect = e.currentTarget.getBoundingClientRect();
    setPopupPosition({ 
      x: rect.left + rect.width/2, 
      y: rect.top + rect.height/2 
    });
    setIsAnimating(true);
    setTimeout(() => {
      setIsAnimating(false);
      setShowTimerDialog(audioId);
    }, 10);
  };

  const handleShowConfirmDialog = (e: React.MouseEvent, audioId: string) => {
    const rect = e.currentTarget.getBoundingClientRect();
    setConfirmPosition({ 
      x: rect.left + rect.width/2, 
      y: rect.top + rect.height/2 
    });
    setIsAnimating(true);
    setTimeout(() => {
      setIsAnimating(false);
      setShowConfirmDialog(audioId);
    }, 10);
  };

  // Adicione este useEffect para gerenciar a seleção de texto
  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      .dragging * {
        user-select: none !important;
        -webkit-user-select: none !important;
        cursor: grabbing !important;
      }
    `;
    document.head.appendChild(style);
    return () => style.remove();
  }, []);

  return (
    <div className="bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg p-3">
      {/* Player Central */}
      <div className="mb-3 bg-[#2d2d2d] rounded-lg p-4 border border-[#404040]">
        {/* Título do Áudio Centralizado */}
        <div className="mb-4 text-center">
          <h3 className="text-[#e1aa1e] font-medium">
            {currentAudio?.title || 'Selecione um áudio'}
          </h3>
        </div>

        <audio
          ref={audioRef}
          src={currentAudio?.url}
          onTimeUpdate={handleTimeUpdate}
          onEnded={handleAudioEnd}
          className="hidden"
        />

        {/* Barra de Progresso */}
        <div
          className="h-1 bg-[#404040] rounded-full cursor-pointer mb-4"
          onClick={handleProgressClick}
        >
          <div
            className="h-full bg-[#e1aa1e] rounded-full"
            style={{ width: `${progress}%` }}
          />
        </div>

        {/* Tempo */}
        <div className="flex justify-between text-xs text-gray-400 mb-3">
          <span>{formatTime(audioRef.current?.currentTime || 0)}</span>
          <span>{formatTime(audioRef.current?.duration || 0)}</span>
        </div>

        {/* Layout em duas linhas */}
        <div className="space-y-4">
          {/* Controle de Reprodução Centralizado */}
          <div className="flex justify-center">
            <button
              onClick={() => currentAudio && togglePlay(currentAudio)}
              className="p-2 hover:bg-[#404040] rounded-full transition-colors"
              disabled={!currentAudio}
            >
              <svg
                className="w-8 h-8 text-[#e1aa1e]"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                {isPlaying ? (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M10 9v6m4-6v6m7-3a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                ) : (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                )}
              </svg>
            </button>
          </div>

          {/* Próxima Reprodução - Alinhado à direita */}
          {(() => {
            const nextInfo = getNextAudioInfo();
            if (!nextInfo?.nextAudio) return null;

            return (
              <div className="flex justify-end">
                <div className="text-right bg-[#1e1e1e] px-3 py-1 rounded">
                  <div className="text-xs text-gray-400">
                    Próximo: {nextInfo.nextAudio.title}
                  </div>
                  {nextInfo.timer && (
                    <div className="text-xs text-[#e1aa1e]">
                      Em {formatTimeLeft(nextInfo.timer.timeLeft)}
                    </div>
                  )}
                </div>
              </div>
            );
          })()}
        </div>
      </div>

      {/* Painel de Controle */}
      <div className="mb-3 bg-[#2d2d2d] rounded-lg p-2 border border-[#404040] flex justify-between items-center">
        <div className="flex items-center gap-2">
          <span className="text-sm text-gray-400 px-2">Lista de Áudios</span>
        </div>

        {/* Botão de Upload */}
        <div className="flex items-center">
          <label className="bg-[#e1aa1e] hover:bg-[#e1aa1e]/80 text-gray-900 px-3 py-1.5 rounded cursor-pointer transition-colors text-sm">
            {isUploading ? 'Enviando...' : 'Anexar Áudio'}
            <input 
              type="file" 
              className="hidden" 
              accept="audio/*" 
              onChange={handleUpload} 
              disabled={isUploading} 
            />
          </label>
        </div>
      </div>

      {/* Lista de Áudios */}
      <div className="space-y-2 max-h-[400px] overflow-y-auto">
        <div className="text-xs text-gray-400 italic mb-2 px-2">
          Clique no ícone de edição ou dê um duplo clique no nome para renomear
        </div>

        {audios.map((audio) => (
          <motion.div
            key={audio.id}
            initial={false}
            className="flex items-center gap-3 p-2 rounded-lg hover:bg-[#2d2d2d] transition-colors min-w-0 bg-[#1e1e1e] mb-2"
            layout
            transition={{
              layout: {
                duration: 0.3,
                ease: [0.25, 1, 0.5, 1]
              }
            }}
          >
            <button
              onClick={() => togglePlay(audio)}
              className="p-1 hover:bg-[#404040] rounded-full transition-colors"
            >
              <svg
                className="w-6 h-6 text-[#e1aa1e]"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                {isPlaying && currentAudio?.id === audio.id ? (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M10 9v6m4-6v6m7-3a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                ) : (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                )}
              </svg>
            </button>

            {editingAudioId === audio.id ? (
              <input
                type="text"
                value={editingTitle}
                onChange={(e) => setEditingTitle(e.target.value)}
                onBlur={() => {
                  if (editingTitle.trim() && editingTitle !== audio.title) {
                    handleUpdateTitle(audio.id, editingTitle);
                  } else {
                    setEditingAudioId(null);
                  }
                }}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    if (editingTitle.trim() && editingTitle !== audio.title) {
                      handleUpdateTitle(audio.id, editingTitle);
                    } else {
                      setEditingAudioId(null);
                    }
                  } else if (e.key === 'Escape') {
                    setEditingAudioId(null);
                  }
                }}
                className="flex-1 bg-[#1e1e1e] text-gray-200 px-2 py-1 rounded border border-[#404040] focus:border-[#e1aa1e] focus:outline-none"
                autoFocus
              />
            ) : (
              <div className="flex-1 flex items-center group">
                <div className="flex-1 flex items-center">
                <span 
                    className="truncate cursor-pointer hover:text-[#e1aa1e]"
                  onDoubleClick={() => {
                    setEditingAudioId(audio.id);
                    setEditingTitle(audio.title);
                  }}
                >
                  {audio.title}
                </span>
                <button
                  onClick={() => {
                    setEditingAudioId(audio.id);
                    setEditingTitle(audio.title);
                  }}
                    className="p-1 rounded-full hover:bg-[#404040] opacity-0 group-hover:opacity-100 transition-all ml-1"
                  title="Editar nome"
                >
                  <svg
                      className="w-4 h-4 text-[#e1aa1e]"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z"
                    />
                  </svg>
                </button>
                </div>
              </div>
            )}
            
            <div className="flex items-center gap-2 shrink-0">
                {audioTimers[audio.id] ? (
                  <button
                    onClick={(e) => {
                      handleShowConfirmDialog(e, audio.id);
                    }}
                    className="flex items-center gap-1.5 px-2 py-1 rounded bg-[#e1aa1e]/10 text-[#e1aa1e] hover:bg-[#e1aa1e]/20 transition-colors"
                    title="Clique para desativar a repetição"
                  >
                    <svg
                      className="w-4 h-4"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"
                      />
                    </svg>
                    <span className="text-sm">
                      {formatTimeLeft(audioTimers[audio.id].timeLeft)}
                    </span>
                  </button>
                ) : (
                  <button
                    onClick={(e) => {
                      handleShowTimerDialog(e, audio.id);
                    }}
                    className="p-1.5 hover:bg-[#404040] rounded transition-colors text-gray-400 hover:text-[#e1aa1e]"
                    title="Ativar repetição automática"
                  >
                    <svg
                      className="w-4 h-4"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"
                      />
                    </svg>
                  </button>
                )}

              <button
                onClick={() => handleDelete(audio.id)}
                className="p-1 hover:bg-[#404040] rounded-full transition-colors text-red-400 hover:text-red-300"
              >
                <svg
                  className="w-5 h-5"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                  />
                </svg>
              </button>
            </div>
          </motion.div>
        ))}
      </div>

      {showTimerDialog && (
        <SetTimerDialog
          audioId={showTimerDialog}
          audioTitle={audios.find(a => a.id === showTimerDialog)?.title || ''}
          onConfirm={(minutes) => {
            startRepeatTimer(showTimerDialog, minutes);
            setShowTimerDialog(null);
            setPopupPosition(null);
            setTimerInputValue('');
          }}
          onCancel={() => {
            setShowTimerDialog(null);
            setPopupPosition(null);
            setTimerInputValue('');
          }}
          buttonPosition={popupPosition}
          inputValue={timerInputValue}
          onInputChange={setTimerInputValue}
        />
      )}

      {showConfirmDialog && (
        <ConfirmDialog
          audioId={showConfirmDialog}
          onConfirm={() => {
            stopRepeatTimer(showConfirmDialog);
            setShowConfirmDialog(null);
            setConfirmPosition(null);
          }}
          onCancel={() => {
            setShowConfirmDialog(null);
            setConfirmPosition(null);
          }}
          buttonPosition={confirmPosition}
        />
      )}
      </div>
    
  );
};

export default AudioPlayer;
