import React, { useState, useEffect } from 'react';
import { videoService, VideoData } from '../lib/supabase';

const VideoPlayer = () => {
  const [videos, setVideos] = useState<VideoData[]>([]);
  const [newVideoUrl, setNewVideoUrl] = useState('');

  useEffect(() => {
    loadVideos();
  }, []);

  const loadVideos = async () => {
    try {
      const data = await videoService.getRecentVideos();
      setVideos(data);
    } catch (error) {
      console.error('Erro ao carregar vídeos:', error);
    }
  };

  // Função para converter URL do YouTube em formato embed
  const getEmbedUrl = (url: string) => {
    try {
      // Para URLs do YouTube
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com') 
          ? url.split('v=')[1]?.split('&')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];
        
        return `https://www.youtube.com/embed/${videoId}`;
      }
      // Para URLs do Vimeo
      if (url.includes('vimeo.com')) {
        const videoId = url.split('vimeo.com/')[1]?.split('?')[0];
        return `https://player.vimeo.com/video/${videoId}`;
      }
      return url;
    } catch (error) {
      console.error('Erro ao processar URL:', error);
      return url;
    }
  };

  const handleAddVideo = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newVideoUrl.trim()) return;

    try {
      const embedUrl = getEmbedUrl(newVideoUrl);
      const newVideo = await videoService.addVideo(embedUrl);
      setVideos(prev => [newVideo, ...prev]);
      setNewVideoUrl('');
    } catch (error) {
      console.error('Erro ao adicionar vídeo:', error);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-4">
      <h2 className="text-xl font-semibold mb-4">Vídeos Recentes</h2>
      <form onSubmit={handleAddVideo} className="mb-4">
        <div className="flex gap-2">
          <input
            type="url"
            value={newVideoUrl}
            onChange={(e) => setNewVideoUrl(e.target.value)}
            placeholder="Cole a URL do YouTube ou Vimeo aqui"
            className="flex-1 border rounded px-3 py-2"
          />
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
          >
            Adicionar
          </button>
        </div>
      </form>
      <div className="space-y-4">
        {videos.map((video) => (
          <div key={video.id} className="p-4 border rounded">
            <div className="relative pt-[56.25%]">
              <iframe
                src={video.url}
                className="absolute top-0 left-0 w-full h-full rounded"
                allowFullScreen
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                title={`Video ${video.id}`}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideoPlayer;
