import React, { useState, useEffect, useRef } from 'react';
import { audioService, AudioData } from '../lib/supabase';
import { supabase } from '../lib/supabase';
import '../styles/animations.css';

interface AudioTimer {
  interval: number;
  timeLeft: number;
  intervalId: NodeJS.Timeout;
  endTime: string;
}

interface AudioPlayerProps {
  onEnded: () => void;
  isPlaying: boolean;
  setIsPlaying: (playing: boolean) => void;
}

const AudioPlayer: React.FC<AudioPlayerProps> = ({ onEnded, isPlaying, setIsPlaying }) => {
  const [audios, setAudios] = useState<AudioData[]>([]);
  const [currentAudio, setCurrentAudio] = useState<AudioData | null>(null);
  const [progress, setProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [audioTimers, setAudioTimers] = useState<Record<string, AudioTimer>>({});
  const timersRef = useRef<Record<string, NodeJS.Timeout>>({});
  const [timerAudioId, setTimerAudioId] = useState<string | null>(null);
  const [pendingAudioId, setPendingAudioId] = useState<string | null>(null);
  const [pendingAudios, setPendingAudios] = useState<string[]>([]);
  const playAttemptRef = useRef<NodeJS.Timeout | null>(null);
  const [editingAudioId, setEditingAudioId] = useState<string | null>(null);
  const [editingTitle, setEditingTitle] = useState('');
  const [showConfirmDialog, setShowConfirmDialog] = useState<string | null>(null);
  const [showTimerDialog, setShowTimerDialog] = useState<string | null>(null);
  const [popupPosition, setPopupPosition] = useState<{ x: number; y: number } | null>(null);
  const [confirmPosition, setConfirmPosition] = useState<{ x: number; y: number } | null>(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const [timerInputValue, setTimerInputValue] = useState('');
  const [preferences, setPreferences] = useState<Record<string, {
    auto_repeat: boolean;
    repeat_interval: number;
    last_played_at?: string;
    play_count: number;
  }>>({});
  const [playAttempts, setPlayAttempts] = useState(0);
  const MAX_PLAY_ATTEMPTS = 3;
  const [showDeleteConfirm, setShowDeleteConfirm] = useState<string | null>(null);
  const [deletePosition, setDeletePosition] = useState<{ x: number; y: number } | null>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationRef = useRef<number>();
  const audioContextRef = useRef<AudioContext>();
  const analyserRef = useRef<AnalyserNode>();

  useEffect(() => {
    loadAudios();
    setupAudioContext();

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      audioContextRef.current?.close();
    };
  }, []);

  const setupAudioContext = () => {
    audioContextRef.current = new (window.AudioContext || (window as any).webkitAudioContext)();
    analyserRef.current = audioContextRef.current.createAnalyser();
    analyserRef.current.fftSize = 256;
  };

  const connectAudioSource = () => {
    if (!audioRef.current || !audioContextRef.current || !analyserRef.current) return;

    const source = audioContextRef.current.createMediaElementSource(audioRef.current);
    source.connect(analyserRef.current);
    analyserRef.current.connect(audioContextRef.current.destination);
  };

  const animate = () => {
    if (!canvasRef.current || !analyserRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const bufferLength = analyserRef.current.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const draw = () => {
      animationRef.current = requestAnimationFrame(draw);
      analyserRef.current!.getByteFrequencyData(dataArray);

      ctx.fillStyle = '#1e1e1e';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const centerY = canvas.height / 2;
      const waveCount = 3;

      for (let wave = 0; wave < waveCount; wave++) {
        ctx.beginPath();
        ctx.moveTo(0, centerY);

        for (let x = 0; x < canvas.width; x++) {
          const index = Math.floor((x / canvas.width) * bufferLength);
          const percent = dataArray[index] / 255;

          // Amplitude mais pronunciada
          const amplitude = isPlaying 
            ? (canvas.height * 0.3 * percent) 
            : (canvas.height * 0.1);

          // Frequência mais dinâmica
          const frequency = 0.02 + (wave * 0.01);

          // Velocidade de movimento aumentada
          const phase = Date.now() * 0.003 + (wave * Math.PI * 0.5);

          // Movimento mais complexo
          const y = centerY + 
                   Math.sin(x * frequency + phase) * amplitude * 
                   (1 + Math.sin(x * 0.01 + phase * 0.5)) * 0.5;

          ctx.lineTo(x, y);
        }

        // Gradiente mais suave
        const gradient = ctx.createLinearGradient(0, 0, 0, canvas.height);
        const alpha = isPlaying ? (0.3 - wave * 0.08) : 0.1;

        gradient.addColorStop(0, `rgba(225, 170, 30, ${alpha * 0.3})`);
        gradient.addColorStop(0.5, `rgba(225, 170, 30, ${alpha})`);
        gradient.addColorStop(1, `rgba(225, 170, 30, ${alpha * 0.3})`);

        ctx.strokeStyle = isPlaying ? 
          `rgba(225, 170, 30, ${0.9 - wave * 0.2})` : 
          `rgba(225, 170, 30, 0.3)`;
        ctx.lineWidth = 2;

        ctx.stroke();
        ctx.closePath();
      }
    };

    draw();
  };

  useEffect(() => {
    if (isPlaying) {
      audioRef.current?.play();
      animate();
    } else {
      audioRef.current?.pause();
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    }
  }, [isPlaying]);

  useEffect(() => {
    const playAudio = async () => {
      if (!audioRef.current) return;

      try {
        console.log('Tentando reproduzir áudio...');
        await audioRef.current.play();
        console.log('Áudio iniciado com sucesso');
        window.dispatchEvent(new Event('audioPlay'));
      } catch (error: unknown) {
        console.error('Erro ao reproduzir áudio:', error);
        if (error instanceof Error && error.name === 'AbortError') {
          // Limpa qualquer tentativa pendente
          if (playAttemptRef.current) {
            clearTimeout(playAttemptRef.current);
          }

          // Tenta novamente após um delay
          playAttemptRef.current = setTimeout(async () => {
            try {
              console.log('Tentando reproduzir novamente...');
              await audioRef.current?.play();
              console.log('Áudio iniciado na segunda tentativa');
              window.dispatchEvent(new Event('audioPlay'));
            } catch (retryError) {
              console.error('Erro na segunda tentativa:', retryError);
              setIsPlaying(false);
            }
          }, 500);
        } else {
          setIsPlaying(false);
        }
      }
    };

    if (audioRef.current) {
      if (isPlaying) {
        // Limpa qualquer tentativa pendente antes de iniciar uma nova
        if (playAttemptRef.current) {
          clearTimeout(playAttemptRef.current);
          playAttemptRef.current = null;
        }

        // Adiciona um pequeno delay antes de tentar reproduzir
        playAttemptRef.current = setTimeout(() => {
          playAudio();
        }, 100);
      } else {
        // Limpa qualquer tentativa pendente
        if (playAttemptRef.current) {
          clearTimeout(playAttemptRef.current);
          playAttemptRef.current = null;
        }
        audioRef.current.pause();
        window.dispatchEvent(new Event('audioStop'));
      }
    }

    // Cleanup
    return () => {
      if (playAttemptRef.current) {
        clearTimeout(playAttemptRef.current);
        playAttemptRef.current = null;
      }
    };
  }, [isPlaying]);

  useEffect(() => {
    return () => {
      Object.values(timersRef.current).forEach(timer => {
        clearInterval(timer);
      });
    };
  }, []);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.onended = () => {
        console.log('Áudio terminou (evento nativo)');
        handleAudioEnd();
      };

      audioRef.current.onloadeddata = () => {
        if (isPlaying) {
          audioRef.current?.play().catch(error => {
            console.error('Erro ao reproduzir áudio após carregamento:', error);
            setIsPlaying(false);
          });
        }
      };
    }
  }, [audioRef.current]);

  useEffect(() => {
    Object.entries(audioTimers).forEach(([audioId, timer]) => {
      const currentTime = Date.now();
      const endTime = new Date(timer.endTime).getTime();
      const timeLeft = Math.max(0, Math.floor((endTime - currentTime) / 1000));

      if (timeLeft === 0) {
        console.log('Timer zerou, iniciando novo ciclo e reprodução');

        // Calcula o novo tempo de término para o próximo ciclo
        const newEndTime = new Date(Date.now() + timer.interval * 1000).toISOString();

        // Atualiza as preferências no banco antes de iniciar o novo ciclo
        audioService.updatePreferences(audioId, {
          auto_repeat: true,
          repeat_interval: Math.floor(timer.interval / 60),
          timer_end_at: newEndTime
        }).catch(error => {
          console.error('Erro ao atualizar preferências do timer:', error);
        });

        // Inicia um novo ciclo
        startNewTimerCycle(audioId, timer.interval);

        // Encontra e reproduz o áudio
        const audio = audios.find(a => a.id === audioId);
        if (audio) {
          if (!isPlaying || (currentAudio?.id === audioId)) {
            const attemptPlay = async (attempts = 0): Promise<void> => {
              try {
                setCurrentAudio(audio);
                if (audioRef.current) {
                  audioRef.current.currentTime = 0;
                  await audioRef.current.play();
                  setIsPlaying(true);
                  setPlayAttempts(0);
                  console.log('Áudio iniciado com sucesso');
                }
              } catch (error) {
                console.error(`Erro ao reproduzir áudio (tentativa ${attempts + 1}):`, error);

                if (error instanceof Error && error.name === 'AbortError' && attempts < MAX_PLAY_ATTEMPTS) {
                  console.log(`Tentando novamente em 500ms (tentativa ${attempts + 1})`);
                  setPlayAttempts(attempts + 1);

                  await new Promise(resolve => setTimeout(resolve, 500));
                  return attemptPlay(attempts + 1);
                }

                setPendingAudios(prev => 
                  prev.includes(audioId) ? prev : [...prev, audioId]
                );
              }
            };

            attemptPlay();
          } else {
            setPendingAudios(prev => {
              if (prev.includes(audioId)) return prev;
              return [...prev, audioId];
            });
          }
        }
      }
    });
  }, [audioTimers, audios, isPlaying, currentAudio]);

  useEffect(() => {
    const updateInterval = setInterval(() => {
      audios.forEach(audio => {
        const prefs = preferences[audio.id];
        if (prefs?.auto_repeat && audioTimers[audio.id]) {
          const timer = audioTimers[audio.id];
          const currentTime = Date.now();
          const endTime = new Date(timer.endTime).getTime();
          const timeLeft = Math.max(0, Math.floor((endTime - currentTime) / 1000));

          if (timeLeft !== timer.timeLeft) {
            setAudioTimers(prev => ({
              ...prev,
              [audio.id]: {
                ...timer,
                timeLeft
              }
            }));
          }
        }
      });
    }, 1000);

    return () => clearInterval(updateInterval);
  }, [audios, preferences, audioTimers]);

  const loadAudios = async () => {
    try {
      const data = await audioService.getAudios();
      setAudios(data);

      // Carrega as preferências para cada áudio
      data.forEach(audio => {
        loadAudioPreferences(audio.id);
      });
    } catch (error) {
      console.error('Erro ao carregar áudios:', error);
    }
  };

  const loadAudioPreferences = async (audioId: string) => {
    try {
      const prefs = await audioService.loadPreferences(audioId);

      if (prefs.auto_repeat && prefs.timer_end_at) {
        const endTime = new Date(prefs.timer_end_at).getTime();
        const now = Date.now();
        const timeLeft = Math.max(0, Math.floor((endTime - now) / 1000));

        if (timeLeft > 0) {
          const interval = prefs.repeat_interval * 60;

          // Cria um timer temporário que será substituído pelo startNewTimerCycle
          const tempIntervalId = setInterval(() => {}, 1000);

          // Atualiza o timer com o tempo restante real
          setAudioTimers(prev => ({
            ...prev,
            [audioId]: {
              interval,
              timeLeft,
              intervalId: tempIntervalId,
              endTime: prefs.timer_end_at
            }
          }));

          // Inicia o ciclo do timer
          startNewTimerCycle(audioId, interval, prefs.timer_end_at);

          // Limpa o timer temporário
          clearInterval(tempIntervalId);

          console.log(`Restaurando timer para ${audioId} com ${timeLeft}s restantes de ${interval}s totais`);
        } else {
          await audioService.updatePreferences(audioId, {
            auto_repeat: false,
            timer_end_at: undefined,
            repeat_interval: 0
          });
        }
      }

      setPreferences(prev => ({
        ...prev,
        [audioId]: {
          auto_repeat: prefs.auto_repeat || false,
          repeat_interval: prefs.repeat_interval || 0,
          last_played_at: prefs.last_played_at,
          play_count: prefs.play_count || 0
        }
      }));
    } catch (error) {
      console.error('Erro ao carregar preferências:', error);
    }
  };

  const handleTimeUpdate = () => {
    if (audioRef.current) {
      const progress = (audioRef.current.currentTime / audioRef.current.duration) * 100;
      setProgress(progress);
    }
  };

  const handleProgressClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (audioRef.current) {
      const bounds = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - bounds.left;
      const width = bounds.width;
      const percentage = x / width;
      audioRef.current.currentTime = audioRef.current.duration * percentage;
    }
  };

  const startNewTimerCycle = (audioId: string, interval: number, existingEndTime?: string) => {
    const endTime = existingEndTime || new Date(Date.now() + interval * 1000).toISOString();

    if (timersRef.current[audioId]) {
      clearInterval(timersRef.current[audioId]);
    }

    const intervalId = setInterval(() => {
      setAudioTimers(prev => {
        const currentTimer = prev[audioId];
        if (!currentTimer) return prev;

        const currentTime = Date.now();
        const timerEndTime = new Date(currentTimer.endTime).getTime();
        const newTimeLeft = Math.max(0, Math.floor((timerEndTime - currentTime) / 1000));

        if (newTimeLeft === 0) {
          return prev;
        }

        return {
          ...prev,
          [audioId]: {
            ...currentTimer,
            timeLeft: newTimeLeft
          }
        };
      });
    }, 1000);

    setAudioTimers(prev => ({
      ...prev,
      [audioId]: {
        interval,
        timeLeft: existingEndTime ? 
          Math.max(0, Math.floor((new Date(existingEndTime).getTime() - Date.now()) / 1000)) : 
          interval,
        intervalId,
        endTime
      }
    }));

    timersRef.current[audioId] = intervalId;
  };

  const handleAudioEnd = async () => {
    console.log('Áudio terminou, verificando status...');

    if (playAttemptRef.current) {
      clearTimeout(playAttemptRef.current);
      playAttemptRef.current = null;
    }

    if (audioRef.current) {
      audioRef.current.currentTime = 0;
    }
    setProgress(0);

    // Processa a fila de pendentes
    if (pendingAudios.length > 0) {
      const nextAudioId = pendingAudios[0];
      const nextAudio = audios.find(a => a.id === nextAudioId);
      setPendingAudios(prev => prev.slice(1));

      if (nextAudio) {
        const attemptPlay = async (attempts = 0): Promise<void> => {
          try {
            await new Promise(resolve => setTimeout(resolve, 500));
            setCurrentAudio(nextAudio);
            if (audioRef.current) {
              await audioRef.current.play();
              setIsPlaying(true);
              setPlayAttempts(0);
            }
          } catch (error) {
            console.error(`Erro ao reproduzir próximo áudio (tentativa ${attempts + 1}):`, error);

            if (error instanceof Error && error.name === 'AbortError' && attempts < MAX_PLAY_ATTEMPTS) {
              await new Promise(resolve => setTimeout(resolve, 500));
              return attemptPlay(attempts + 1);
            }

            setIsPlaying(false);
            onEnded();
          }
        };

        attemptPlay();
      }
    } else {
      setIsPlaying(false);
      onEnded();
    }

    window.dispatchEvent(new Event('audioStop'));
  };

  const togglePlay = async (audio: AudioData) => {
    try {
      // Se está tentando tocar um áudio diferente do atual
      if (currentAudio?.id !== audio.id) {
        // Para o áudio atual se estiver tocando
        if (isPlaying) {
          setIsPlaying(false);
        }

        // Atualiza o áudio atual
        setCurrentAudio(audio);

        // Pequeno delay para garantir que o áudio foi carregado
        setTimeout(() => {
          if (audioRef.current) {
            audioRef.current.currentTime = 0;
            audioRef.current.play()
              .then(() => {
                setIsPlaying(true);
                setPlayAttempts(0);
              })
              .catch(error => {
                console.error('Erro ao reproduzir áudio:', error);
                setIsPlaying(false);
              });
          }
        }, 100);
      } else {
        // Se é o mesmo áudio, apenas alterna play/pause
        if (isPlaying) {
          audioRef.current?.pause();
          setIsPlaying(false);
        } else {
          audioRef.current?.play()
            .then(() => {
              setIsPlaying(true);
              setPlayAttempts(0);
            })
            .catch(error => {
              console.error('Erro ao reproduzir áudio:', error);
              setIsPlaying(false);
            });
        }
      }
    } catch (error) {
      console.error('Erro ao alternar reprodução:', error);
      setIsPlaying(false);
    }
  };

  const handleUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    try {
      console.log('Iniciando upload do arquivo:', file.name);

      // Upload do arquivo
      const fileExt = file.name.split('.').pop();
      const fileName = `${Math.random()}.${fileExt}`;
      const filePath = `audios/${fileName}`;

      console.log('Tentando fazer upload para:', filePath);

      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('media')
        .upload(filePath, file);

      if (uploadError) {
        console.error('Erro no upload:', uploadError);
        throw uploadError;
      }

      console.log('Upload bem sucedido:', uploadData);

      // Obtém a URL pública
      const { data: { publicUrl } } = supabase.storage
        .from('media')
        .getPublicUrl(filePath);

      console.log('URL pública gerada:', publicUrl);

      // Adiciona ao banco de dados
      const newAudio = await audioService.addAudio({
        title: file.name,
        url: publicUrl,
        auto_repeat: false,
        repeat_interval: 0,
        play_count: 0
