      // Se o áudio sendo editado é o atual, atualiza também o currentAudio
      if (currentAudio?.id === audioId) {
        setCurrentAudio(prev => prev ? { ...prev, title: newTitle } : null);
      }
      setEditingAudioId(null);
    } catch (error) {
