import React, { useState, useEffect } from 'react';
import { audioService, AudioData } from '../lib/supabase';

const AudioPlayer = () => {
  const [audios, setAudios] = useState<AudioData[]>([]);
  const [isUploading, setIsUploading] = useState(false);

  useEffect(() => {
    loadAudios();
  }, []);

  const loadAudios = async () => {
    try {
      const data = await audioService.getAudios();
      setAudios(data);
    } catch (error) {
      console.error('Erro ao carregar áudios:', error);
    }
  };

  const handleUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    try {
      const newAudio = await audioService.addAudio({
        title: file.name,
        url: URL.createObjectURL(file),
        isAutoPlay: false
      });
      setAudios(prev => [newAudio, ...prev]);
    } catch (error) {
      console.error('Erro ao fazer upload:', error);
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-4">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Lista de Áudios</h2>
        <label className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded cursor-pointer">
          {isUploading ? 'Enviando...' : 'Upload'}
          <input
            type="file"
            className="hidden"
            accept="audio/*"
            onChange={handleUpload}
            disabled={isUploading}
          />
        </label>
      </div>
      <div className="space-y-4">
        {audios.map((audio) => (
          <div key={audio.id} className="p-4 border rounded hover:bg-gray-50">
            <h3 className="font-medium mb-2">{audio.title}</h3>
            <audio controls className="w-full">
              <source src={audio.url} type="audio/mpeg" />
              Seu navegador não suporta o elemento de áudio.
            </audio>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AudioPlayer;
