import React, { useState, useRef } from 'react';

interface TextToSpeechProps {
  apiKey: string;
}

const TextToSpeech: React.FC<TextToSpeechProps> = ({ apiKey }) => {
  const [text, setText] = useState('');
  const [audioUrl, setAudioUrl] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const audioRef = useRef<HTMLAudioElement>(null);

  const generateSpeech = async () => {
    if (!text.trim()) {
      setError('Por favor, digite algum texto');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `https://texttospeech.googleapis.com/v1/text:synthesize?key=${apiKey}`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            input: { text },
            voice: {
              languageCode: 'pt-BR',
              name: 'pt-BR-Standard-A',
              ssmlGender: 'FEMALE'
            },
            audioConfig: {
              audioEncoding: 'MP3',
              pitch: 0,
              speakingRate: 1
            },
          }),
        }
      );

      if (!response.ok) {
        throw new Error('Erro ao gerar áudio');
      }

      const { audioContent } = await response.json();
      const audioBlob = new Blob(
        [Uint8Array.from(atob(audioContent), c => c.charCodeAt(0))],
        { type: 'audio/mp3' }
      );
      const url = URL.createObjectURL(audioBlob);
      setAudioUrl(url);
    } catch (err) {
      setError('Erro ao gerar áudio. Tente novamente.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDownload = () => {
    if (audioUrl) {
      const a = document.createElement('a');
      a.href = audioUrl;
      a.download = 'audio.mp3';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  return (
    <div className="bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg p-4 mt-6">
      <h2 className="text-xl font-semibold mb-4 text-[#e1aa1e]">
        Converter Texto em Áudio
      </h2>
      
      <div className="space-y-4">
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Digite o texto que você quer converter em áudio..."
          className="w-full h-32 bg-[#2d2d2d] border border-[#404040] text-gray-200 rounded px-3 py-2 focus:border-[#e1aa1e] focus:outline-none resize-none"
          disabled={isLoading}
        />

        {error && (
          <p className="text-red-400 text-sm">{error}</p>
        )}

        <div className="flex gap-3">
          <button
            onClick={generateSpeech}
            disabled={isLoading}
            className={`flex-1 ${
              isLoading 
                ? 'bg-[#e1aa1e]/50 cursor-not-allowed' 
                : 'bg-[#e1aa1e] hover:bg-[#e1aa1e]/80'
            } text-gray-900 px-4 py-2 rounded transition-colors flex items-center justify-center gap-2`}
          >
            {isLoading ? (
              <>
                <svg className="animate-spin h-5 w-5" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                </svg>
                Gerando...
              </>
            ) : (
              <>
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" />
                </svg>
                Gerar Áudio
              </>
            )}
          </button>

          {audioUrl && (
            <button
              onClick={handleDownload}
              className="bg-[#2d2d2d] hover:bg-[#404040] border border-[#404040] text-[#e1aa1e] px-4 py-2 rounded flex items-center justify-center gap-2 transition-colors"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" />
              </svg>
              Download
            </button>
          )}
        </div>

        {audioUrl && (
          <div className="mt-4 p-4 bg-[#2d2d2d] rounded-lg border border-[#404040]">
            <audio
              ref={audioRef}
              controls
              className="w-full focus:outline-none"
              src={audioUrl}
            >
              Seu navegador não suporta o elemento de áudio.
            </audio>
          </div>
        )}
      </div>
    </div>
  );
};

export default TextToSpeech;
