import React, { useState } from 'react';
import { authService } from '../lib/auth';

interface LoginProps {
  onLoginSuccess: () => void;
}

const Login: React.FC<LoginProps> = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    if (!username || !password) {
      setError('Por favor, preencha todos os campos');
      setLoading(false);
      return;
    }

    try {
      const user = await authService.signIn(username, password);
      if (user) {
        onLoginSuccess();
      } else {
        setError('Usuário ou senha inválidos');
      }
    } catch (err) {
      console.error('Erro no login:', err);
      setError('Erro ao fazer login. Por favor, tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#1e1e1e]">
      <div className="bg-[#2d2d2d] p-8 rounded-lg shadow-lg w-96 border border-[#404040]">
        <h2 className="text-2xl font-semibold mb-6 text-[#e1aa1e] text-center">Império Fitness</h2>

        {error && (
          <div className="mb-4 p-3 bg-red-500/10 border border-red-500/20 rounded text-red-400 text-sm">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-300 text-sm font-medium mb-2">
              Usuário
            </label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300 focus:outline-none focus:border-[#e1aa1e] transition-colors"
              required
            />
          </div>

          <div className="mb-6">
            <label className="block text-gray-300 text-sm font-medium mb-2">
              Senha
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300 focus:outline-none focus:border-[#e1aa1e] transition-colors"
              required
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className={`w-full py-2 px-4 bg-[#e1aa1e] text-gray-900 rounded-md font-medium 
              ${loading ? 'opacity-50 cursor-not-allowed' : 'hover:bg-[#e1aa1e]/80'} 
              transition-colors`}
          >
            {loading ? 'Entrando...' : 'Entrar'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
