import React, { useState, useRef, useEffect } from 'react';

interface TextToSpeechProps {
  apiKey: string;
  isOpen: boolean;
  onClose: () => void;
}

const TextToSpeech: React.FC<TextToSpeechProps> = ({ apiKey, isOpen, onClose }) => {
  const [text, setText] = useState('');
  const [audioUrl, setAudioUrl] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [lastGeneratedText, setLastGeneratedText] = useState<string>('');
  const audioRef = useRef<HTMLAudioElement>(null);

  const notifyAudioStart = () => {
    window.dispatchEvent(new Event('audioPlay'));
  };

  const notifyAudioEnd = () => {
    window.dispatchEvent(new Event('audioStop'));
  };

  const playExistingAudio = async () => {
    if (audioRef.current && audioUrl) {
      try {
        audioRef.current.src = audioUrl;
        audioRef.current.currentTime = 0;
        await audioRef.current.play();
      } catch (error) {
        console.error('Erro ao reproduzir 치udio:', error);
        setLastGeneratedText('');
        setAudioUrl(null);
        generateSpeech();
      }
    }
  };

  const generateSpeech = async () => {
    if (!text.trim()) {
      setError('Por favor, digite algum texto');
      return;
    }

    if (text === lastGeneratedText && audioUrl) {
      playExistingAudio();
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `https://texttospeech.googleapis.com/v1/text:synthesize?key=${apiKey}`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            input: { text },
            voice: {
              languageCode: 'pt-BR',
              name: 'pt-BR-Wavenet-D',
              ssmlGender: 'FEMALE'
            },
            audioConfig: {
              audioEncoding: 'MP3',
              pitch: 1.0,
              speakingRate: 1.05,
              volumeGainDb: 3.0,
              effectsProfileId: ['headphone-class-device']
            },
          }),
        }
      );

      if (!response.ok) {
        throw new Error('Erro ao gerar 치udio');
      }

      const { audioContent } = await response.json();
      const audioBlob = new Blob(
        [Uint8Array.from(atob(audioContent), c => c.charCodeAt(0))],
        { type: 'audio/mp3' }
      );
      const url = URL.createObjectURL(audioBlob);
      setAudioUrl(url);
      setLastGeneratedText(text);

      if (audioRef.current) {
        audioRef.current.src = url;
        audioRef.current.addEventListener('play', notifyAudioStart);
        audioRef.current.addEventListener('ended', notifyAudioEnd);
        audioRef.current.addEventListener('pause', notifyAudioEnd);
        await audioRef.current.play();
      }
    } catch (err) {
      console.error('Erro ao gerar 치udio:', err);
      setError('Erro ao gerar 치udio. Tente novamente.');
      setLastGeneratedText('');
      setAudioUrl(null);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (!isOpen) {
      if (audioRef.current) {
        audioRef.current.pause();
      }
      setError(null);
      setIsLoading(false);
    } else if (audioRef.current && audioUrl) {
      audioRef.current.src = audioUrl;
      audioRef.current.load();
    }

    return () => {
      if (audioRef.current) {
        audioRef.current.removeEventListener('play', notifyAudioStart);
        audioRef.current.removeEventListener('ended', notifyAudioEnd);
        audioRef.current.removeEventListener('pause', notifyAudioEnd);
      }
    };
  }, [isOpen, audioUrl]);

  useEffect(() => {
    return () => {
      if (audioUrl) {
        URL.revokeObjectURL(audioUrl);
      }
    };
  }, [audioUrl]);

  const handleDownload = () => {
    if (audioUrl) {
      const a = document.createElement('a');
      a.href = audioUrl;
      a.download = 'audio.mp3';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="max-w-2xl w-full mx-4 animate-fadeIn">
        <div className="bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg overflow-hidden">
          <div className="bg-[#2d2d2d] px-6 py-4 border-b border-[#404040] flex items-center justify-between">
            <div className="flex items-center gap-3">
              <svg 
                className="w-5 h-5 text-[#e1aa1e]" 
                fill="none" 
                stroke="currentColor" 
                viewBox="0 0 24 24"
              >
                <path 
                  strokeLinecap="round" 
                  strokeLinejoin="round" 
                  strokeWidth={2} 
                  d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" 
                />
              </svg>
              <h2 className="text-lg font-medium text-[#e1aa1e]">
                Digite o texto para falar
              </h2>
            </div>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-200 transition-colors"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="p-6 space-y-4">
            <div className="relative">
              <textarea
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="Digite aqui o texto que voc칡 quer que seja falado..."
                className="w-full h-24 bg-[#2d2d2d] border border-[#404040] text-gray-200 rounded-lg px-4 py-3 focus:border-[#e1aa1e] focus:outline-none resize-none"
                disabled={isLoading}
              />
              {error && (
                <p className="absolute -bottom-6 left-0 text-red-400 text-sm">{error}</p>
              )}
            </div>

            <div className="flex items-center gap-3 pt-2">
              <button
                onClick={generateSpeech}
                disabled={isLoading}
                className={`flex-1 ${
                  isLoading 
                    ? 'bg-[#e1aa1e]/50 cursor-not-allowed' 
                    : 'bg-[#e1aa1e] hover:bg-[#e1aa1e]/80'
                } text-gray-900 px-4 py-2.5 rounded-lg transition-colors flex items-center justify-center gap-2 font-medium whitespace-nowrap`}
              >
                {isLoading ? (
                  <>
                    <svg className="animate-spin h-4 w-4" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                    </svg>
                    <span>Gerando...</span>
                  </>
                ) : (
                  <>
                    <svg className="w-5 h-5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" />
                    </svg>
                    <span className="truncate">
                      {text === lastGeneratedText && audioUrl ? 'Repetir Fala' : 'Falar Texto'}
                    </span>
                  </>
                )}
              </button>

              {audioUrl && (
                <button
                  onClick={handleDownload}
                  className="bg-[#2d2d2d] hover:bg-[#404040] border border-[#404040] text-[#e1aa1e] px-4 py-2.5 rounded-lg flex items-center justify-center gap-2 transition-colors font-medium"
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" />
                  </svg>
                  <span>Baixar</span>
                </button>
              )}
            </div>
          </div>

          <audio ref={audioRef} className="hidden" />
        </div>
      </div>
    </div>
  );
};

export default TextToSpeech;
