import React from 'react';
import { VideoData } from '../lib/supabase';

interface VideoSidebarProps {
  isOpen: boolean;
  onClose: () => void;
  videos: VideoData[];
  onVideoSelect: (video: VideoData) => void;
}

const VideoSidebar: React.FC<VideoSidebarProps> = ({ 
  isOpen, 
  onClose, 
  videos,
  onVideoSelect
}) => {
  const getVideoThumbnail = (url: string) => {
    try {
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com')
          ? url.split('embed/')[1]?.split('?')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];
        return `https://img.youtube.com/vi/${videoId}/mqdefault.jpg`;
      }
      return null;
    } catch {
      return null;
    }
  };

  return (
    <>
      {/* Overlay */}
      {isOpen && (
        <div
          className="absolute inset-0 bg-black/50 backdrop-blur-sm transition-opacity duration-300"
          onClick={onClose}
        />
      )}

      {/* Sidebar */}
      <div
        className={`absolute right-0 top-0 bottom-0 w-[320px] bg-[#1e1e1e]/95 backdrop-blur-sm shadow-2xl transform transition-all duration-300 ease-in-out 
          ${isOpen ? 'translate-x-0' : 'translate-x-full'} overflow-hidden`}
      >
        <div className="h-full flex flex-col">
          {/* Header */}
          <div className="p-4 border-b border-[#404040]">
            <div className="flex justify-between items-center">
              <h3 className="text-lg font-semibold text-[#e1aa1e]">Últimos Vídeos</h3>
              <button
                onClick={onClose}
                className="p-1 hover:bg-[#404040] rounded-full transition-colors text-gray-400 hover:text-[#e1aa1e]"
              >
                <svg
                  className="w-5 h-5"
                  fill="none"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            </div>
          </div>

          {/* Lista de Vídeos */}
          <div className="flex-1 overflow-y-auto custom-scrollbar">
            <div className="p-4 space-y-3">
              {videos.map((video) => {
                const thumbnail = getVideoThumbnail(video.url);
                return (
                  <div
                    key={video.id}
                    onClick={() => {
                      onVideoSelect(video);
                      onClose();
                    }}
                    className="flex gap-3 items-center p-2 hover:bg-[#2d2d2d] rounded-lg cursor-pointer group transition-colors"
                  >
                    {/* Thumbnail */}
                    <div className="relative w-24 flex-shrink-0">
                      <div className="aspect-video rounded-lg overflow-hidden border border-[#404040] group-hover:border-[#e1aa1e]/50 transition-colors">
                        {thumbnail ? (
                          <img
                            src={thumbnail}
                            alt={video.title || 'Thumbnail do vídeo'}
                            className="w-full h-full object-cover transform group-hover:scale-105 transition-transform duration-300"
                          />
                        ) : (
                          <div className="w-full h-full bg-[#2d2d2d] flex items-center justify-center">
                            <svg
                              className="w-8 h-8 text-gray-400 group-hover:text-[#e1aa1e] transition-colors"
                              fill="none"
                              stroke="currentColor"
                              viewBox="0 0 24 24"
                            >
                              <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth={2}
                                d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"
                              />
                            </svg>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Título */}
                    <div className="flex-1 min-w-0">
                      <h4 className="text-sm font-medium text-[#e1aa1e] group-hover:text-[#e1aa1e]/80 transition-colors line-clamp-2">
                        {video.title || 'Vídeo sem título'}
                      </h4>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default VideoSidebar;
