import React, { useState } from 'react';
import { User } from '../types/auth';
import { authService } from '../lib/auth';
import UserSettings from './UserSettings';

interface HeaderProps {
  user: User | null;
  onLogout: () => void;
  onUserUpdate: () => Promise<void>;
}

const Header: React.FC<HeaderProps> = ({ user, onLogout, onUserUpdate }) => {
  const [showSettings, setShowSettings] = useState(false);

  const handleLogout = async () => {
    try {
      await authService.signOut();
      onLogout();
    } catch (error) {
      console.error('Erro ao fazer logout:', error);
    }
  };

  // Para debug
  console.log('Dados do usuário no Header:', user);

  return (
    <header className="bg-[#1e1e1e] shadow-lg fixed w-full top-0 z-50 border-b border-[#404040]">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-[#e1aa1e] text-center flex-grow">
          <span className="transition-transform duration-300 hover:scale-105">Império Fitness</span>
        </h1>
        
        {user && (
          <div className="flex items-center gap-4">
            <span className="text-gray-300">
              Olá, {user.display_name || user.full_name}
            </span>
            {user.role === 'admin' && (
              <button
                onClick={() => setShowSettings(true)}
                className="px-3 py-1.5 text-sm bg-[#e1aa1e] text-gray-900 rounded hover:bg-[#e1aa1e]/80 transition-colors"
                title="Configurações de Usuário"
              >
                Configurações
              </button>
            )}
            <button
              onClick={handleLogout}
              className="px-3 py-1.5 text-sm bg-[#404040] text-gray-300 rounded hover:bg-[#505050] transition-colors"
            >
              Sair
            </button>
          </div>
        )}
      </div>

      {showSettings && user && user.role === 'admin' && (
        <UserSettings
          user={user}
          onClose={() => setShowSettings(false)}
          onSuccess={onUserUpdate}
        />
      )}
    </header>
  );
};

export default Header;
