import React, { useState, useEffect, useRef } from 'react';
import { audioService, AudioData } from '../lib/supabase';
import { supabase } from '../lib/supabase';
import '../styles/animations.css';
import TextToSpeech from './TextToSpeech';
import { ConfirmDialog } from './dialogs/ConfirmDialog';
import { TimerDialog } from './dialogs/TimerDialog';
import { DeleteConfirmDialog } from './dialogs/DeleteConfirmDialog';

interface AudioTimer {
  interval: number;
  timeLeft: number;
  intervalId: NodeJS.Timeout;
  endTime: string;
}

interface AudioPlayerProps {
  onEnded: () => void;
  isPlaying: boolean;
  setIsPlaying: (playing: boolean) => void;
}

const AudioPlayer: React.FC<AudioPlayerProps> = ({ onEnded, isPlaying, setIsPlaying }) => {
  const [audios, setAudios] = useState<AudioData[]>([]);
  const [currentAudio, setCurrentAudio] = useState<AudioData | null>(null);
  const [progress, setProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const audioRef = useRef<HTMLAudioElement>(null);
  const [audioTimers, setAudioTimers] = useState<Record<string, AudioTimer>>({});
  const timersRef = useRef<Record<string, NodeJS.Timeout>>({});
  const [timerAudioId, setTimerAudioId] = useState<string | null>(null);
  const [pendingAudioId, setPendingAudioId] = useState<string | null>(null);
  const [pendingAudios, setPendingAudios] = useState<string[]>([]);
  const playAttemptRef = useRef<NodeJS.Timeout | null>(null);
  const [editingAudioId, setEditingAudioId] = useState<string | null>(null);
  const [editingTitle, setEditingTitle] = useState('');
  const [showConfirmDialog, setShowConfirmDialog] = useState<string | null>(null);
  const [showTimerDialog, setShowTimerDialog] = useState<string | null>(null);
  const [popupPosition, setPopupPosition] = useState<{ x: number; y: number } | null>(null);
  const [confirmPosition, setConfirmPosition] = useState<{ x: number; y: number } | null>(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const [timerInputValue, setTimerInputValue] = useState('');
  const [preferences, setPreferences] = useState<Record<string, {
    auto_repeat: boolean;
    repeat_interval: number;
    last_played_at?: string;
    play_count: number;
  }>>({});
  const [playAttempts, setPlayAttempts] = useState(0);
  const MAX_PLAY_ATTEMPTS = 3;
  const [showDeleteConfirm, setShowDeleteConfirm] = useState<string | null>(null);
  const [deletePosition, setDeletePosition] = useState<{ x: number; y: number } | null>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const analyserRef = useRef<AnalyserNode | null>(null);
  const animationRef = useRef<number | null>(null);
  const isPlayingRef = useRef(false);
  const endTimeRefs = useRef<Record<string, string>>({});
  const [showTextToSpeech, setShowTextToSpeech] = useState(false);
  const [isTextToSpeechPlaying, setIsTextToSpeechPlaying] = useState(false);
  const mainCanvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const loadInitialAudios = async () => {
      try {
        const data = await audioService.getAudios();
        setAudios(data);

        // Carrega as preferências para cada áudio
        data.forEach(audio => {
          loadAudioPreferences(audio.id);
        });
      } catch (error) {
        console.error('Erro ao carregar áudios:', error);
      }
    };

    loadInitialAudios();
  }, []);

  const loadAudioSafely = async (audioElement: HTMLAudioElement, url: string): Promise<void> => {
    return new Promise((resolve, reject) => {
      let resolved = false;

      const loadHandler = () => {
        if (resolved) return;
        resolved = true;
        cleanup();
        resolve();
      };

      const errorHandler = (e: Event) => {
        if (resolved) return;
        resolved = true;
        cleanup();
        reject(e);
      };

      const cleanup = () => {
        audioElement.removeEventListener('canplaythrough', loadHandler);
        audioElement.removeEventListener('loadeddata', loadHandler);
        audioElement.removeEventListener('error', errorHandler);
      };

      audioElement.addEventListener('canplaythrough', loadHandler);
      audioElement.addEventListener('loadeddata', loadHandler);
      audioElement.addEventListener('error', errorHandler);

      // Timeout para evitar espera infinita
      setTimeout(() => {
        if (!resolved) {
          resolved = true;
          cleanup();
          resolve(); // Resolve mesmo com timeout para tentar reproduzir
        }
      }, 5000);

      audioElement.src = url;
      audioElement.load();
    });
  };

  const stopCurrentAudio = async () => {
    if (audioRef.current) {
      const currentAudioElement = audioRef.current;

      try {
        // Remove todos os event listeners antes de qualquer operação
        currentAudioElement.removeEventListener('ended', handleAudioEnd);
        currentAudioElement.removeEventListener('timeupdate', handleTimeUpdate);

        // Para a reprodução
        if (!currentAudioElement.paused) {
          const pausePromise = currentAudioElement.pause();
          if (pausePromise !== undefined) {
            await pausePromise;
          }
        }

        // Reseta o estado
        currentAudioElement.currentTime = 0;
        isPlayingRef.current = false;
        setIsPlaying(false);

        // Notifica que o áudio parou
        window.dispatchEvent(new Event('audioStop'));

        // Limpa a fonte
        currentAudioElement.removeAttribute('src');
        currentAudioElement.load();

        // Aguarda um momento para garantir que tudo foi limpo
        await new Promise(resolve => setTimeout(resolve, 100));
      } catch (error) {
        console.error('Erro ao parar áudio atual:', error);
      }
    }
  };

  const handleAudioEnd = () => {
    isPlayingRef.current = false;
    setIsPlaying(false);
    window.dispatchEvent(new Event('audioStop'));
  };

  const togglePlay = async (audio: AudioData) => {
    if (isLoading) return;

    try {
      setIsLoading(true);

      // Verifica se o contexto de áudio precisa ser reiniciado
      if (analyserRef.current?.context.state === 'closed') {
        // Recria o contexto de áudio
        const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)() as AudioContext;
        const analyser = audioContext.createAnalyser();
        analyserRef.current = analyser;

        if (audioRef.current) {
          const source = audioContext.createMediaElementSource(audioRef.current);
          source.connect(analyser);
          analyser.connect(audioContext.destination);
        }
      }

      // Se clicou em um áudio diferente do atual
      if (currentAudio?.id !== audio.id) {
        // Para o áudio atual primeiro
        if (audioRef.current) {
          try {
            await audioRef.current.pause();
            audioRef.current.currentTime = 0;
          } catch (error) {
            console.error('Erro ao parar áudio atual:', error);
          }
        }

        // Define o novo áudio
        setCurrentAudio(audio);

        if (audioRef.current) {
          const audioElement = audioRef.current;

          try {
            // Configura a fonte do áudio
            audioElement.src = audio.url;

            // Aguarda o áudio estar pronto
            await new Promise((resolve) => {
              const handleCanPlay = () => {
                audioElement.removeEventListener('canplay', handleCanPlay);
                resolve(true);
              };
              audioElement.addEventListener('canplay', handleCanPlay);
            });

            // Dispara evento antes de iniciar a reprodução
            window.dispatchEvent(new Event('audioPlay'));

            // Inicia a reprodução com tratamento de promessa
            try {
              const playPromise = audioElement.play();
              if (playPromise !== undefined) {
                await playPromise;
                isPlayingRef.current = true;
                setIsPlaying(true);
              }
            } catch (error) {
              console.error('Erro ao reproduzir áudio:', error);
              isPlayingRef.current = false;
              setIsPlaying(false);
              window.dispatchEvent(new Event('audioStop'));
            }
          } catch (error) {
            console.error('Erro ao configurar áudio:', error);
            await stopCurrentAudio();
          }
        }
      } else {
        // Mesmo áudio - alterna play/pause
        if (audioRef.current) {
          const audioElement = audioRef.current;

          try {
            if (isPlayingRef.current) {
              const pausePromise = audioElement.pause();
              if (pausePromise !== undefined) {
                await pausePromise;
              }
              isPlayingRef.current = false;
              setIsPlaying(false);
              window.dispatchEvent(new Event('audioStop'));
            } else {
              window.dispatchEvent(new Event('audioPlay'));
              const playPromise = audioElement.play();
              if (playPromise !== undefined) {
                await playPromise;
                isPlayingRef.current = true;
                setIsPlaying(true);
              }
            }
          } catch (error) {
            console.error('Erro ao alternar reprodução:', error);
            await stopCurrentAudio();
          }
        }
      }
    } catch (error) {
      console.error('Erro ao alternar reprodução:', error);
      await stopCurrentAudio();
    } finally {
      setIsLoading(false);
    }
  };

  const handleTimeUpdate = () => {
    if (audioRef.current && !audioRef.current.paused) {
      const currentTime = audioRef.current.currentTime;
      const duration = audioRef.current.duration;
      setProgress((currentTime / duration) * 100);
    }
  };

  const loadAudios = async () => {
    try {
      const data = await audioService.getAudios();
      setAudios(data);

      // Carrega as preferências para cada áudio
      data.forEach(audio => {
        loadAudioPreferences(audio.id);
      });
    } catch (error) {
      console.error('Erro ao carregar áudios:', error);
    }
  };

  const loadAudioPreferences = async (audioId: string) => {
    try {
      const prefs = await audioService.loadPreferences(audioId);

      if (prefs.auto_repeat && prefs.timer_end_at) {
        const endTime = new Date(prefs.timer_end_at).getTime();
        const now = Date.now();
        const timeLeft = Math.max(0, Math.floor((endTime - now) / 1000));

        // Se ainda há tempo restante ou se acabou de terminar
        if (timeLeft >= 0) {
          const interval = prefs.repeat_interval * 60;

          // Se o timer acabou de terminar, cria um novo ciclo
          if (timeLeft === 0) {
            const newEndTime = new Date(Date.now() + interval * 1000).toISOString();

            // Atualiza o endTime no banco
            await audioService.updatePreferences(audioId, {
              ...prefs,
              timer_end_at: newEndTime
            });

            // Inicia novo ciclo com o novo endTime
            startNewTimerCycle(audioId, interval, newEndTime);
          } else {
            // Continua o ciclo atual com o tempo restante
            startNewTimerCycle(audioId, interval, prefs.timer_end_at);
          }
        } else {
          // Se o timer expirou há muito tempo, desativa-o
          await audioService.updatePreferences(audioId, {
            auto_repeat: false,
            repeat_interval: 0,
            timer_end_at: undefined
          });
        }
      }

      // Atualiza as preferências locais
      setPreferences(prev => ({
        ...prev,
        [audioId]: {
          auto_repeat: prefs.auto_repeat || false,
          repeat_interval: prefs.repeat_interval || 0,
          last_played_at: prefs.last_played_at,
          play_count: prefs.play_count || 0
        }
      }));

    } catch (error) {
      // Mantém o erro silencioso
    }
  };

  const startNewTimerCycle = (audioId: string, interval: number, existingEndTime?: string) => {
    endTimeRefs.current[audioId] = existingEndTime || new Date(Date.now() + interval * 1000).toISOString();
    const initialTimeLeft = Math.max(0, Math.floor((new Date(endTimeRefs.current[audioId]).getTime() - Date.now()) / 1000));

    if (timersRef.current[audioId]) {
      clearInterval(timersRef.current[audioId]);
    }

    let attemptCount = 0;
    const MAX_ATTEMPTS = 3;

    const createNewCycle = async () => {
      try {
        if (isPlayingRef.current) {
          if (attemptCount < MAX_ATTEMPTS) {
            attemptCount++;
            setTimeout(() => createNewCycle(), 1000);
          } else {
            isPlayingRef.current = false;
          }
          return;
        }

        isPlayingRef.current = true;
        attemptCount = 0;

        const audioData = await audioService.getAudio(audioId);
        if (!audioData) {
          isPlayingRef.current = false;
          return;
        }

        // Primeiro atualiza as preferências
        const newEndTime = new Date(Date.now() + interval * 1000).toISOString();
        await audioService.updatePreferences(audioId, {
          auto_repeat: true,
          repeat_interval: interval / 60,
          timer_end_at: newEndTime
        });

        endTimeRefs.current[audioId] = newEndTime;

        // Depois prepara o áudio
        if (audioRef.current) {
          const audioElement = audioRef.current;
          audioElement.src = audioData.url;
          audioElement.currentTime = 0;

          // Aguarda o áudio estar pronto
          await new Promise((resolve) => {
            audioElement.addEventListener('canplay', resolve, { once: true });
          });

          setCurrentAudio(audioData);
          setIsPlaying(true);

          try {
            await audioElement.play();
          } catch (error) {
            isPlayingRef.current = false;
            setIsPlaying(false);
            window.dispatchEvent(new Event('audioStop'));
          }
        }

        // Libera para próxima reprodução quando este áudio terminar
        if (audioRef.current) {
          const audioElement = audioRef.current;
          audioElement.addEventListener('ended', () => {
            isPlayingRef.current = false;
            setIsPlaying(false);
          }, { once: true });
        }

      } catch (error) {
        isPlayingRef.current = false;
        setIsPlaying(false);
      }
    };

    const intervalId = setInterval(() => {
      const currentTime = Date.now();
      const timerEndTime = new Date(endTimeRefs.current[audioId]).getTime();
      const newTimeLeft = Math.max(0, Math.floor((timerEndTime - currentTime) / 1000));

      if (newTimeLeft === 0 && !isPlayingRef.current) {
        createNewCycle();
      }

      setAudioTimers(prev => ({
        ...prev,
        [audioId]: {
          interval,
          timeLeft: newTimeLeft,
          intervalId,
          endTime: endTimeRefs.current[audioId]
        }
      }));
    }, 1000);

    timersRef.current[audioId] = intervalId;

    setAudioTimers(prev => ({
      ...prev,
      [audioId]: {
        interval,
        timeLeft: initialTimeLeft,
        intervalId,
        endTime: endTimeRefs.current[audioId]
      }
    }));

    if (initialTimeLeft === 0) {
      createNewCycle();
    }

    return intervalId;
  };

  const handleProgressClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (audioRef.current) {
      const bounds = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - bounds.left;
      const width = bounds.width;
      const percentage = x / width;
      audioRef.current.currentTime = audioRef.current.duration * percentage;
    }
  };

  const handleUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    try {
      console.log('Iniciando upload do arquivo:', file.name);

