    }}>
      <div 
        className="absolute bg-[#2d2d2d] p-4 rounded-lg border border-[#404040] shadow-lg w-64"
        style={{
          top: buttonPosition ? `${buttonPosition.y - 80}px` : '50%',
          left: buttonPosition ? `${buttonPosition.x - 100}px` : '50%',
          transform: isAnimating ? 'scale(0.9)' : 'scale(1)',
          opacity: isAnimating ? 0 : 1,
          transition: 'transform 0.2s ease-out, opacity 0.2s ease-out'
        }}
      >
        <h3 className="text-red-500 font-medium text-sm mb-2">Confirmar Exclusão</h3>
        <p className="text-gray-300 text-xs mb-4">
          Tem certeza que deseja excluir este áudio?
        </p>
        <div className="flex justify-end gap-2">
          <button
            onClick={onCancel}
            className="px-3 py-1.5 rounded bg-[#404040] text-xs text-gray-200 hover:bg-[#505050] transition-colors"
          >
            Cancelar
          </button>
          <button
            onClick={onConfirm}
            className="px-3 py-1.5 rounded bg-red-500 text-xs text-white hover:bg-red-600 transition-colors"
          >
            Excluir
          </button>
        </div>
      </div>
    </div>
  );

  useEffect(() => {
    if (audioRef.current && canvasRef.current) {
      if (isPlaying) {
        audioRef.current.play();
        canvasRef.current.getContext('2d')?.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      } else {
        audioRef.current.pause();
        canvasRef.current.getContext('2d')?.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      }
    }
  }, [isPlaying]);

  const animate = () => {
    if (!mainCanvasRef.current || !analyserRef.current) return;

    const canvas = mainCanvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const bufferLength = analyserRef.current.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const draw = () => {
      animationRef.current = requestAnimationFrame(draw);
      analyserRef.current!.getByteFrequencyData(dataArray);

      // Fundo com gradiente
      ctx.fillStyle = '#1e1e1e';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Número de ondas e configurações
      const waves = 3;
      const points = 100;
      const spacing = canvas.height / (waves + 1);

      // Para cada linha de onda
      for (let w = 1; w <= waves; w++) {
        const baseY = spacing * w;

        ctx.beginPath();
        ctx.moveTo(0, baseY);

        // Desenha a onda através da largura do canvas
        for (let x = 0; x <= canvas.width; x++) {
          const progress = x / canvas.width;
          const index = Math.floor(progress * bufferLength);
          const value = dataArray[index] / 255.0;

          // Amplitude varia com o áudio
          const amplitude = isPlaying ? 30 * value : 10;

          // Frequência e fase variam com o tempo e posição
          const frequency = 0.02;
          const phase = Date.now() * 0.002 + w;

          // Calcula a posição Y com múltiplas ondas sobrepostas
          const y = baseY + 
                   Math.sin(x * frequency + phase) * amplitude +
                   Math.sin(x * frequency * 0.5 + phase * 1.5) * amplitude * 0.5;

          ctx.lineTo(x, y);
        }

        // Gradiente para a onda
        const gradient = ctx.createLinearGradient(0, baseY - 50, 0, baseY + 50);
        const alpha = isPlaying ? (0.5 - (w - 1) * 0.1) : 0.2;

        gradient.addColorStop(0, `rgba(225, 170, 30, 0)`);
        gradient.addColorStop(0.5, `rgba(225, 170, 30, ${alpha})`);
        gradient.addColorStop(1, `rgba(225, 170, 30, 0)`);

        ctx.strokeStyle = gradient;
        ctx.lineWidth = 2;
        ctx.stroke();

        // Efeito de brilho
        if (isPlaying) {
          ctx.save();
          ctx.filter = 'blur(4px)';
          ctx.strokeStyle = `rgba(225, 170, 30, ${alpha * 0.5})`;
          ctx.lineWidth = 4;
          ctx.stroke();
          ctx.restore();
        }
      }

      // Adiciona partículas brilhantes
      if (isPlaying) {
        for (let i = 0; i < bufferLength; i += 8) {
          const value = dataArray[i] / 255.0;
          if (value > 0.5) {
            const x = Math.random() * canvas.width;
            const y = Math.random() * canvas.height;
            const size = Math.random() * 3 * value;

            ctx.beginPath();
            ctx.arc(x, y, size, 0, Math.PI * 2);
            ctx.fillStyle = `rgba(225, 170, 30, ${value * 0.3})`;
            ctx.fill();
          }
        }
      }
    };

    draw();
  };

  // Modifique o useEffect que gerencia eventos de vídeo
  useEffect(() => {
    const handleVideoPlay = async () => {
      if (audioRef.current && !audioRef.current.paused) {
        console.log('Vídeo iniciado - pausando áudio');
        try {
          await stopCurrentAudio(); // Use stopCurrentAudio em vez de pause direto
        } catch (error) {
          console.error('Erro ao pausar áudio:', error);
        }
      }
    };

    window.addEventListener('videoPlay', handleVideoPlay);

    return () => {
      window.removeEventListener('videoPlay', handleVideoPlay);
    };
  }, []);

  // Adicione este useEffect para limpar o áudio quando o componente é desmontado
  useEffect(() => {
    return () => {
      const cleanup = async () => {
        try {
          if (audioRef.current) {
            // Primeiro tenta pausar
            if (!audioRef.current.paused) {
              const pausePromise = audioRef.current.pause();
              if (pausePromise !== undefined) {
                await pausePromise;
              }
            }

            // Limpa a fonte e estado
            audioRef.current.src = '';
            audioRef.current.load();
            isPlayingRef.current = false;
            setIsPlaying(false);
          }

          // Limpa o contexto de áudio se existir
          if (analyserRef.current) {
            try {
              // Desconecta todos os nós
              analyserRef.current.disconnect();

              // Reseta as conexões
              if (audioRef.current) {
                try {
                  // Cast para AudioContext para acessar createMediaElementSource
                  const audioContext = analyserRef.current.context as AudioContext;
                  const mediaSource = audioContext.createMediaElementSource(audioRef.current);
                  mediaSource.disconnect();
                } catch (error) {
                  // Ignora erro se a fonte já estiver desconectada
                  console.log('Fonte de áudio já desconectada');
                }
              }
            } catch (error) {
              console.error('Erro ao limpar conexões de áudio:', error);
            } finally {
              // Limpa a referência
              analyserRef.current = null;
            }
          }

          // Cancela qualquer animação pendente
          if (animationRef.current) {
            cancelAnimationFrame(animationRef.current);
          }

          // Dispara evento de parada
          window.dispatchEvent(new Event('audioStop'));
        } catch (error) {
          console.error('Erro durante cleanup:', error);
        }
      };

      cleanup();
    };
  }, []);

  const handleTitleSubmit = async () => {
    if (!editingAudioId) return;

    try {
      await audioService.updateAudioTitle(editingAudioId, editingTitle);
      setAudios(prev => prev.map(audio => 
        audio.id === editingAudioId ? { ...audio, title: editingTitle } : audio
      ));
    } catch (error) {
      console.error('Erro ao atualizar título:', error);
    }

    setEditingAudioId(null);
  };

  const handleTimerClick = (e: React.MouseEvent, audioId: string) => {
    const button = e.currentTarget as HTMLButtonElement;
    const rect = button.getBoundingClientRect();
    setPopupPosition({ x: rect.left, y: rect.bottom + 5 });
    setShowTimerDialog(audioId);
  };

  const handleOptionsClick = (e: React.MouseEvent, audioId: string) => {
    const button = e.currentTarget as HTMLButtonElement;
    const rect = button.getBoundingClientRect();
    setConfirmPosition({ x: rect.left, y: rect.bottom + 5 });
    setShowConfirmDialog(audioId);
  };

  const handleDeleteClick = (e: React.MouseEvent, audioId: string) => {
    const button = e.currentTarget as HTMLButtonElement;
    const rect = button.getBoundingClientRect();
    setDeletePosition({ x: rect.left, y: rect.bottom + 5 });
    setShowDeleteConfirm(audioId);
  };

  const handleConfirm = async () => {
    if (!showConfirmDialog) return;

    try {
      const audio = audios.find(a => a.id === showConfirmDialog);
      if (!audio) return;

      const currentPrefs = preferences[audio.id] || {
        auto_repeat: false,
        repeat_interval: 0,
        play_count: 0
      };

      await audioService.updateAudioPreferences(audio.id, {
        ...currentPrefs,
        auto_repeat: !currentPrefs.auto_repeat
      });

      loadAudioPreferences(audio.id);
    } catch (error) {
      console.error('Erro ao atualizar preferências:', error);
    }

    setShowConfirmDialog(null);
    setConfirmPosition(null);
  };

  const handleTimerConfirm = async () => {
    if (!showTimerDialog || !timerInputValue) return;

    try {
      const minutes = parseInt(timerInputValue);
      if (isNaN(minutes) || minutes <= 0) return;

      const endTime = new Date();
      endTime.setMinutes(endTime.getMinutes() + minutes);

      setTimerAudioId(showTimerDialog);
      endTimeRefs.current[showTimerDialog] = `${minutes}m`;

      const timeoutId = setTimeout(() => {
        const audio = audios.find(a => a.id === showTimerDialog);
        if (audio) {
          togglePlay(audio);
        }
        delete endTimeRefs.current[showTimerDialog];
        setTimerAudioId(null);
      }, minutes * 60 * 1000);

      timersRef.current[showTimerDialog] = timeoutId;
    } catch (error) {
      console.error('Erro ao configurar timer:', error);
    }

    setShowTimerDialog(null);
    setPopupPosition(null);
    setTimerInputValue('');
  };

  return (
    <div className="bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg p-4">
      <div className="mb-6">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-medium text-[#e1aa1e]">Áudios</h2>
          <canvas ref={canvasRef} className="w-32 h-8" />
        </div>

        {/* Player Principal com Visualizador */}
        <div className="mb-4 bg-[#2d2d2d] rounded-lg border border-[#404040] overflow-hidden">
          {/* Título do Áudio */}
          <div className="p-3 border-b border-[#404040]">
            <h3 className="text-[#e1aa1e] font-medium text-center">
              {currentAudio?.title || 'Selecione um áudio'}
            </h3>
          </div>

          {/* Área do Visualizador */}
          <div className="relative h-32 bg-[#1e1e1e] p-4">
            <canvas
              ref={mainCanvasRef}
              className="absolute inset-0 w-full h-full"
              width={1000}
              height={200}
            />

            {/* Botão de Play centralizado */}
            <div className="absolute inset-0 flex items-center justify-center">
              <button
                onClick={() => {
                  if (currentAudio) {
                    setIsPlaying(!isPlaying);
                    if (isPlaying) {
                      audioRef.current?.pause();
                    } else {
                      audioRef.current?.play();
                    }
                  }
                }}
                className={`p-4 rounded-full bg-[#e1aa1e]/90 hover:bg-[#e1aa1e] transition-all transform 
                  hover:scale-105 active:scale-95 ${isPlaying ? 'animate-pulse' : ''}`}
                disabled={!currentAudio}
              >
                <svg
                  className="w-8 h-8 text-gray-900"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  {isPlaying ? (
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2.5}
                      d="M10 9v6m4-6v6m7-3a9 9 0 11-18 0 9 9 0 0118 0z"
                    />
                  ) : (
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2.5}
                      d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                    />
                  )}
                </svg>
              </button>
            </div>
          </div>

          {/* Controles e Progresso */}
          <div className="p-4 bg-[#1e1e1e]/50">
            <audio
              ref={audioRef}
              src={currentAudio?.url}
              onTimeUpdate={handleTimeUpdate}
              onEnded={handleAudioEnd}
              onPlay={() => window.dispatchEvent(new Event('audioPlay'))}
              onPause={() => window.dispatchEvent(new Event('audioStop'))}
              className="hidden"
            />

            {/* Barra de Progresso */}
            <div
              className="h-1 bg-[#404040] rounded-full cursor-pointer mb-2"
              onClick={handleProgressClick}
            >
              <div
                className="h-full bg-[#e1aa1e] rounded-full"
                style={{ width: `${progress}%` }}
              />
            </div>

            {/* Tempo */}
            <div className="flex justify-between text-xs text-gray-400 mb-3">
              <span>{formatTime(audioRef.current?.currentTime || 0)}</span>
              <span>{formatTime(audioRef.current?.duration || 0)}</span>
            </div>

            {/* Próxima Reprodução */}
            {(() => {
              const nextInfo = getNextAudioInfo();
              if (!nextInfo?.nextAudio) return null;

              return (
                <div className="flex justify-end">
                  <div className="text-right bg-[#2d2d2d] px-3 py-1 rounded border border-[#404040]/50">
                    <div className="text-xs text-gray-400">
                      Próximo: {nextInfo.nextAudio.title}
                    </div>
                    {nextInfo.timer && (
                      <div className="text-xs text-[#e1aa1e]">
                        Em {formatTimeLeft(nextInfo.timer.timeLeft)}
                      </div>
                    )}
                  </div>
                </div>
              );
            })()}
          </div>
        </div>

        {/* Cabeçalho da Lista */}
        <div className="mb-3 bg-[#2d2d2d] rounded-lg p-2 border border-[#404040] flex justify-between items-center">
          <div className="flex items-center gap-2">
            <span className="text-sm text-gray-400 px-2">Lista de Áudios</span>
          </div>

          {/* Botões de Upload e Text-to-Speech */}
          <div className="flex items-center gap-4">
            <button
              onClick={() => setShowTextToSpeech(true)}
              className="bg-[#2d2d2d] hover:bg-[#404040] border border-[#404040] px-4 py-2.5 rounded-lg transition-colors flex items-center justify-center gap-2"
            >
              {isTextToSpeechPlaying ? (
                <>
                  <div className="relative">
                    <div className="absolute -inset-1 bg-[#e1aa1e]/20 rounded-full animate-ping" />
                    <div className="absolute -inset-2 bg-[#e1aa1e]/10 rounded-full animate-ping animation-delay-150" />
                    <div className="absolute -inset-3 bg-[#e1aa1e]/5 rounded-full animate-ping animation-delay-300" />

                    <svg 
                      className="w-5 h-5 text-[#e1aa1e] relative z-10 animate-wave" 
                      fill="none" 
                      stroke="currentColor" 
                      viewBox="0 0 24 24"
                    >
                      <path 
                        strokeLinecap="round" 
                        strokeLinejoin="round" 
                        strokeWidth={2} 
                        d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" 
                      />
                    </svg>
                  </div>
                  <span className="text-[#e1aa1e] font-medium relative">
                    <span className="animate-pulse">Falando</span>
                    <span className="absolute -right-6 top-1/2 -translate-y-1/2 flex space-x-0.5">
                      <span className="w-1 h-1 bg-[#e1aa1e] rounded-full animate-bounce delay-0" />
                      <span className="w-1 h-1 bg-[#e1aa1e] rounded-full animate-bounce delay-150" />
                      <span className="w-1 h-1 bg-[#e1aa1e] rounded-full animate-bounce delay-300" />
                    </span>
                  </span>
                </>
              ) : (
                <>
                  <svg 
                    className="w-5 h-5 text-[#e1aa1e] transition-transform duration-300 hover:scale-110" 
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                  >
                    <path 
                      strokeLinecap="round" 
                      strokeLinejoin="round" 
                      strokeWidth={2} 
                      d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" 
                    />
                  </svg>
                  <span className="text-[#e1aa1e]">Falar Texto</span>
                </>
              )}
            </button>

            <label className="bg-[#e1aa1e] hover:bg-[#e1aa1e]/80 text-gray-900 px-3 py-1.5 rounded cursor-pointer transition-colors text-sm">
              {isUploading ? 'Enviando...' : 'Anexar Áudio'}
              <input
                type="file"
                className="hidden"
                accept="audio/*"
                onChange={handleUpload}
                disabled={isUploading}
              />
            </label>
          </div>
        </div>
      </div>

      {/* Lista de Áudios */}
      <div className="space-y-2 max-h-[400px] overflow-y-auto">
        <div className="text-xs text-gray-400 italic mb-2 px-2">
          Clique no ícone de edição ou dê um duplo clique no nome para renomear
        </div>

        {audios.map((audio) => (
          <div
            key={audio.id}
            className="flex items-center gap-3 p-2 rounded-lg hover:bg-[#2d2d2d] transition-colors min-w-0 bg-[#1e1e1e] mb-2"
          >
            <button
              onClick={() => togglePlay(audio)}
              className="p-1 hover:bg-[#404040] rounded-full transition-colors"
            >
              <svg
                className="w-6 h-6 text-[#e1aa1e]"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                {isPlaying && currentAudio?.id === audio.id ? (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2.5}
                    d="M10 9v6m4-6v6m7-3a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                ) : (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2.5}
                    d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                )}
              </svg>
            </button>

            {editingAudioId === audio.id ? (
              <input
                type="text"
                value={editingTitle}
                onChange={(e) => setEditingTitle(e.target.value)}
                onBlur={handleTitleSubmit}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    handleTitleSubmit();
                  } else if (e.key === 'Escape') {
                    setEditingAudioId(null);
                  }
                }}
                className="flex-1 bg-[#2d2d2d] border border-[#404040] text-gray-200 rounded px-2 py-1 min-w-0"
                autoFocus
              />
            ) : (
              <div
                className="flex-1 truncate cursor-pointer"
                onDoubleClick={() => {
                  setEditingAudioId(audio.id);
                  setEditingTitle(audio.title);
                }}
              >
                {audio.title}
              </div>
            )}

            <div className="flex items-center gap-2">
              {audioTimers[audio.id] ? (
                <button
                  onClick={(e) => {
                    handleShowConfirmDialog(e, audio.id);
                  }}
                  className="flex items-center gap-1.5 px-2 py-1 rounded bg-[#e1aa1e]/10 text-[#e1aa1e] hover:bg-[#e1aa1e]/20 transition-colors"
                  title="Clique para desativar a repetição"
                >
                  <svg
                    className="w-4 h-4"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"
                    />
                  </svg>
                  <span className="text-sm">
                    {formatTimeLeft(audioTimers[audio.id].timeLeft)}
                  </span>
                </button>
              ) : (
                <button
                  onClick={(e) => {
                    handleShowTimerDialog(e, audio.id);
                  }}
                  className="p-1.5 hover:bg-[#404040] rounded transition-colors text-gray-400 hover:text-[#e1aa1e]"
                  title="Ativar repetição automática"
                >
                  <svg
                    className="w-4 h-4"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"
                    />
                  </svg>
                </button>
              )}

              <button
                onClick={(e) => handleOptionsClick(e, audio.id)}
                className="p-1 hover:bg-[#404040] rounded transition-colors"
                title="Opções"
              >
                <svg
                  className="w-5 h-5 text-gray-400 hover:text-[#e1aa1e]"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M5 12h.01M12 12h.01M19 12h.01M6 12a1 1 0 11-2 0 1 1 0 012 0zm7 0a1 1 0 11-2 0 1 1 0 012 0zm7 0a1 1 0 11-2 0 1 1 0 012 0z"
                  />
                </svg>
              </button>

              <button
                onClick={(e) => handleDeleteClick(e, audio.id)}
                className="p-1 hover:bg-[#404040] rounded transition-colors"
                title="Excluir"
              >
                <svg
                  className="w-5 h-5 text-gray-400 hover:text-red-500"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                  />
                </svg>
              </button>
            </div>
          </div>
        ))}
      </div>

      {showConfirmDialog && (
        <ConfirmDialog
          audioId={showConfirmDialog}
          onConfirm={handleConfirm}
          onEdit={() => {
            setShowConfirmDialog(null);
            setConfirmPosition(null);
          }}
          onCancel={() => {
            setShowConfirmDialog(null);
            setConfirmPosition(null);
          }}
          buttonPosition={confirmPosition}
        />
      )}

      {showTimerDialog && (
        <TimerDialog
          audioId={showTimerDialog}
          onConfirm={handleTimerConfirm}
          onCancel={() => {
            setShowTimerDialog(null);
            setPopupPosition(null);
          }}
          buttonPosition={popupPosition}
          initialValue={timerInputValue}
          onValueChange={setTimerInputValue}
        />
      )}

      {showDeleteConfirm && (
        <DeleteConfirmDialog
          audioId={showDeleteConfirm}
          onConfirm={() => {
            handleDelete(showDeleteConfirm);
            setShowDeleteConfirm(null);
            setDeletePosition(null);
          }}
          onCancel={() => {
            setShowDeleteConfirm(null);
            setDeletePosition(null);
          }}
          buttonPosition={deletePosition}
        />
      )}

      <TextToSpeech 
        apiKey="AIzaSyBvkopJSt0VaBRbqfdtevWDuCwVRDhNZ2o"
        isOpen={showTextToSpeech}
        onClose={() => setShowTextToSpeech(false)}
        onPlayingChange={setIsTextToSpeechPlaying}
      />
    </div>
  );
};

export default AudioPlayer;
