import { supabase } from './supabase';
import { User } from '../types/auth';

export const authService = {
  async signIn(username: string, password: string): Promise<User | null> {
    if (!username || !password) return null;

    try {
      console.log('Tentando login com:', { username });

      const { data, error } = await supabase
        .rpc('check_password', {
          p_username: username,
          p_password: password
        });

      console.log('Resposta do Supabase:', { data, error });

      if (error) {
        console.error('Erro na autenticação:', error);
        throw error;
      }
      
      if (data && data.length > 0) {
        const user = data[0];
        console.log('Usuário encontrado:', { ...user, password: '[REDACTED]' });
        localStorage.setItem('user', JSON.stringify(user));
        return user;
      }

      console.log('Nenhum usuário encontrado');
      return null;
    } catch (error) {
      console.error('Erro ao fazer login:', error);
      throw error;
    }
  },

  async getCurrentUser(): Promise<User | null> {
    try {
      const userData = localStorage.getItem('user');
      if (!userData) return null;

      const user = JSON.parse(userData);
      
      // Busca dados atualizados do usuário
      const { data, error } = await supabase
        .from('users')
        .select('id, username, full_name, role, display_name')
        .eq('username', user.username)
        .single();

      if (error || !data) {
        localStorage.removeItem('user');
        return null;
      }

      // Atualiza o localStorage com os dados mais recentes
      localStorage.setItem('user', JSON.stringify(data));
      return data;

    } catch {
      localStorage.removeItem('user');
      return null;
    }
  },

  async signOut() {
    localStorage.removeItem('user');
  },

  async changePassword(username: string, oldPassword: string, newPassword: string): Promise<boolean> {
    const { data, error } = await supabase
      .rpc('change_password', {
        p_username: username,
        p_old_password: oldPassword,
        p_new_password: newPassword
      });

    if (error) throw error;
    return data || false;
  },

  async updateUsername(currentUsername: string, newUsername: string, adminPassword: string): Promise<boolean> {
    const { data, error } = await supabase
      .rpc('update_username', {
        p_current_username: currentUsername,
        p_new_username: newUsername,
        p_admin_password: adminPassword
      });

    if (error) throw error;
    return data || false;
  },

  async updateDisplayName(username: string, newDisplayName: string, adminPassword: string): Promise<boolean> {
    const { data, error } = await supabase
      .rpc('update_display_name', {
        p_username: username,
        p_display_name: newDisplayName,
        p_admin_password: adminPassword
      });

    if (error) throw error;
    return data || false;
  }
};
