import React, { useState, useEffect } from 'react';
import { authService } from '../lib/auth';

interface LoginProps {
  onLoginSuccess: () => void;
}

const Login: React.FC<LoginProps> = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [loginAttempts, setLoginAttempts] = useState(0);
  const [isBlocked, setIsBlocked] = useState(false);
  const [blockTimer, setBlockTimer] = useState(0);

  useEffect(() => {
    const blockedUntil = localStorage.getItem('loginBlockedUntil');
    if (blockedUntil && new Date(blockedUntil) > new Date()) {
      setIsBlocked(true);
      startBlockTimer(new Date(blockedUntil));
    }
  }, []);

  const startBlockTimer = (endTime: Date) => {
    const interval = setInterval(() => {
      const remaining = Math.ceil((endTime.getTime() - new Date().getTime()) / 1000);
      if (remaining <= 0) {
        setIsBlocked(false);
        setBlockTimer(0);
        clearInterval(interval);
        localStorage.removeItem('loginBlockedUntil');
      } else {
        setBlockTimer(remaining);
      }
    }, 1000);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    if (!username.trim() || !password.trim()) {
      setError('Por favor, preencha todos os campos');
      return;
    }

    if (isBlocked) {
      setError(`Tente novamente em ${blockTimer} segundos`);
      return;
    }

    try {
      setLoading(true);
      const user = await authService.signIn(username, password);
      
      if (user) {
        setLoginAttempts(0);
        localStorage.removeItem('loginAttempts');
        onLoginSuccess();
      } else {
        handleFailedLogin();
      }
    } catch (err) {
      console.error('Erro no login:', err);
      handleFailedLogin();
    } finally {
      setLoading(false);
    }
  };

  const handleFailedLogin = () => {
    const attempts = loginAttempts + 1;
    setLoginAttempts(attempts);
    
    if (attempts >= 5) {
      const blockUntil = new Date(Date.now() + 5 * 60 * 1000);
      localStorage.setItem('loginBlockedUntil', blockUntil.toISOString());
      setIsBlocked(true);
      startBlockTimer(blockUntil);
      setError('Muitas tentativas. Tente novamente em 5 minutos.');
    } else {
      setError('Usuário ou senha inválidos');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-[#1e1e1e] to-[#141414] animate-fadeIn">
      <div className="bg-[#2d2d2d]/90 backdrop-blur-sm p-8 rounded-lg shadow-2xl w-96 border border-[#404040] animate-slideUp">
        <div className="animate-scaleIn">
          <h2 className="text-3xl font-bold mb-2 text-[#e1aa1e] text-center">Império Fitness</h2>
          <p className="text-gray-400 text-sm text-center mb-6">Área restrita</p>
        </div>
        
        {error && (
          <div className="mb-4 p-3 bg-red-500/10 border border-red-500/20 rounded text-red-400 text-sm animate-slideLeft">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-300 text-sm font-medium mb-2">
              Usuário
            </label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300 focus:outline-none focus:border-[#e1aa1e] focus:ring-1 focus:ring-[#e1aa1e] transition-all hover:border-[#505050]"
              required
              disabled={isBlocked}
              autoComplete="username"
            />
          </div>

          <div>
            <label className="block text-gray-300 text-sm font-medium mb-2">
              Senha
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-3 py-2 bg-[#1e1e1e] border border-[#404040] rounded-md text-gray-300 focus:outline-none focus:border-[#e1aa1e] focus:ring-1 focus:ring-[#e1aa1e] transition-all hover:border-[#505050]"
              required
              disabled={isBlocked}
              autoComplete="current-password"
            />
          </div>

          <button
            type="submit"
            disabled={loading || isBlocked}
            className={`w-full py-2 px-4 bg-[#e1aa1e] text-gray-900 rounded-md font-medium 
              ${loading || isBlocked ? 'opacity-50 cursor-not-allowed' : 'hover:bg-[#e1aa1e]/90 transform hover:scale-[1.02] active:scale-[0.98]'} 
              transition-all duration-200 ease-in-out`}
          >
            {loading ? (
              <span className="flex items-center justify-center">
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-gray-900" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Entrando...
              </span>
            ) : isBlocked ? (
              `Bloqueado (${blockTimer}s)`
            ) : (
              'Entrar'
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
