import React from 'react';
import { VideoData } from '../lib/supabase';

interface VideoSidebarProps {
  isOpen: boolean;
  onClose: () => void;
  videos: VideoData[];
  onVideoSelect: (video: VideoData) => void;
}

const VideoSidebar: React.FC<VideoSidebarProps> = ({ 
  isOpen, 
  onClose, 
  videos,
  onVideoSelect
}) => {
  const getVideoThumbnail = (url: string) => {
    try {
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com')
          ? url.split('embed/')[1]?.split('?')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];
        return `https://img.youtube.com/vi/${videoId}/mqdefault.jpg`;
      }
      return null;
    } catch {
      return null;
    }
  };

  if (!isOpen) return null;

  return (
    <>
      {/* Overlay */}
      <div
        className="fixed inset-0 bg-black/50 backdrop-blur-sm transition-opacity duration-500"
        onClick={onClose}
      />

      {/* Sidebar */}
      <div
        className="fixed right-0 top-0 h-screen w-[320px] bg-[#1e1e1e] shadow-2xl animate-slideInRight"
      >
        {/* Header fixo */}
        <div className="absolute top-0 left-0 right-0 bg-[#1e1e1e] border-b border-[#404040] z-10">
          <div className="px-6 py-5 flex justify-between items-center">
            <h3 className="text-xl font-semibold text-[#e1aa1e]">Últimos Vídeos</h3>
            <button
              onClick={onClose}
              className="p-2 hover:bg-[#404040] rounded-full transition-colors text-gray-400 hover:text-[#e1aa1e]"
            >
              <svg
                className="w-6 h-6"
                fill="none"
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path d="M6 18L18 6M6 6l12 12"></path>
              </svg>
            </button>
          </div>
        </div>

        {/* Lista com scroll */}
        <div className="h-full pt-[76px] overflow-hidden">
          <div className="h-full overflow-y-auto custom-scrollbar">
            <div className="px-4 py-3 space-y-3">
              {videos.map((video) => {
                const thumbnail = getVideoThumbnail(video.url);
                return (
                  <div
                    key={video.id}
                    className="flex gap-2 p-2 hover:bg-[#2d2d2d] cursor-pointer rounded-lg group transition-colors"
                    onClick={() => {
                      onVideoSelect(video);
                      onClose();
                    }}
                  >
                    {thumbnail ? (
                      <img
                        src={thumbnail}
                        alt={video.title || 'Thumbnail do vídeo'}
                        className="w-28 h-16 object-cover rounded border border-[#404040]"
                      />
                    ) : (
                      <div className="w-28 h-16 bg-[#2d2d2d] rounded border border-[#404040] flex items-center justify-center">
                        <svg
                          className="w-8 h-8 text-gray-400"
                          fill="none"
                          stroke="currentColor"
                          viewBox="0 0 24 24"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth={2}
                            d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"
                          />
                        </svg>
                      </div>
                    )}
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-[#e1aa1e] truncate group-hover:text-[#e1aa1e]/80">
                        {video.title || 'Vídeo sem título'}
                      </p>
                      <p className="text-xs text-gray-400 truncate">
                        {new Date(video.created_at).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default VideoSidebar;
