import React from 'react';
import { User } from '../types/auth';
import { authService } from '../lib/auth';

interface HeaderProps {
  user: User | null;
  onLogout: () => void;
  onUserUpdate: () => Promise<void>;
}

const Header: React.FC<HeaderProps> = ({ user, onLogout, onUserUpdate }) => {
  const handleLogout = async () => {
    try {
      await authService.signOut();
      onLogout();
    } catch (error) {
      console.error('Erro ao fazer logout:', error);
    }
  };

  return (
    <header className="bg-[#2d2d2d] p-4">
      <h1 className="text-2xl font-bold text-[#e1aa1e] text-center">
        <span className="transition-transform duration-300 hover:scale-105">Império Fitness</span>
      </h1>
      {user && (
        <div className="flex justify-between items-center">
          <span className="text-gray-300">Olá, {user.display_name || user.full_name}</span>
          <button onClick={handleLogout} className="text-gray-300 hover:text-[#e1aa1e]">
            Sair
          </button>
        </div>
      )}
    </header>
  );
};

export default Header;
