import React, { useState, useEffect } from 'react';
import { videoService, VideoData } from '../lib/supabase';
import VideoSidebar from './VideoSidebar';

interface VideoPlayerProps {
  videoId: string;
  onEnded?: () => void;
  autoplay?: boolean;
  isPlaying?: boolean;
  setIsPlaying?: (playing: boolean) => void;
  shouldAutoplay?: boolean;
}

export const VideoPlayer: React.FC<VideoPlayerProps> = ({ 
  videoId, 
  onEnded,
  autoplay = false,
  isPlaying = false,
  setIsPlaying = () => {},
  shouldAutoplay = false
}) => {
  const opts = {
    height: '100%',
    width: '100%',
    playerVars: {
      autoplay: autoplay ? 1 : 0,
      controls: 1,
      rel: 0,
    },
  };

  const [videos, setVideos] = useState<VideoData[]>([]);
  const [newVideoUrl, setNewVideoUrl] = useState('');
  const [error, setError] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [selectedVideo, setSelectedVideo] = useState<VideoData | null>(null);
  const [player, setPlayer] = useState<any>(null);
  const [isPlayerReady, setIsPlayerReady] = useState(false);
  const [localIsPlaying, setLocalIsPlaying] = useState(isPlaying);

  useEffect(() => {
    setLocalIsPlaying(isPlaying);
  }, [isPlaying]);

  const updatePlayingState = (state: boolean) => {
    setLocalIsPlaying(state);
    setIsPlaying(state);
  };

  useEffect(() => {
    loadVideos();
  }, []);

  const loadYouTubeAPI = () => {
    return new Promise<void>((resolve) => {
      const existingScript = document.querySelector('script[src="https://www.youtube.com/iframe_api"]');
      if (existingScript) {
        if ((window as any).YT && (window as any).YT.Player) {
          resolve();
          return;
        }
      }

      const tag = document.createElement('script');
      tag.src = 'https://www.youtube.com/iframe_api';
      tag.async = true;
      const firstScriptTag = document.getElementsByTagName('script')[0];
      firstScriptTag.parentNode?.insertBefore(tag, firstScriptTag);

      (window as any).onYouTubeIframeAPIReady = () => {
        resolve();
      };
    });
  };

  const initializePlayer = async (videoId?: string, shouldAutoplay: boolean = false) => {
    try {
      await loadYouTubeAPI();

      if (!(window as any).YT?.Player) {
        console.error('API do YouTube não está disponível');
        return null;
      }

      if (player) {
        try {
          player.destroy();
        } catch (e) {
          console.error('Erro ao destruir player existente:', e);
        }
        setPlayer(null);
      }

      const playerContainer = document.getElementById('youtube-player');
      if (!playerContainer) {
        console.error('Container do player não encontrado');
        return null;
      }

      while (playerContainer.firstChild) {
        playerContainer.removeChild(playerContainer.firstChild);
      }

      const currentVideoId = videoId || extractVideoId(selectedVideo?.url || videos[0]?.url);
      if (!currentVideoId) {
        console.error('VideoId não encontrado');
        return null;
      }

      const newPlayer = new (window as any).YT.Player('youtube-player', {
        videoId: currentVideoId,
        height: '100%',
        width: '100%',
        events: {
          onReady: (event: any) => handlePlayerReady(event, shouldAutoplay),
          onStateChange: handleStateChange,
          onError: (error: any) => {
            console.error('Erro do player:', error);
          }
        },
        playerVars: {
          enablejsapi: 1,
          origin: window.location.origin,
          widget_referrer: window.location.origin,
          playsinline: 1,
          controls: 1,
          rel: 0,
          modestbranding: 1,
          autoplay: shouldAutoplay ? 1 : 0
        }
      });

      setPlayer(newPlayer);
      return newPlayer;
    } catch (error) {
      console.error('Erro ao inicializar player:', error);
      return null;
    }
  };

  useEffect(() => {
    if (selectedVideo || videos[0]) {
      const videoId = extractVideoId(selectedVideo?.url || videos[0]?.url);
      if (videoId) {
        initializePlayer(videoId, shouldAutoplay);
      }
    }

    return () => {
      if (player) {
        try {
          player.destroy();
          setPlayer(null);
          setIsPlayerReady(false);
        } catch (error) {
          console.error('Erro ao destruir player:', error);
        }
      }
    };
  }, [selectedVideo?.url, videos[0]?.url]);

  const loadVideos = async () => {
    try {
      const data = await videoService.getRecentVideos();
      setVideos(data);
    } catch (error) {
      console.error('Erro ao carregar vídeos:', error);
    }
  };

  // Função para converter URL do YouTube em formato embed
  const getEmbedUrl = (url: string) => {
    console.log('URL original:', url);
    try {
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com') 
          ? url.split('v=')[1]?.split('&')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];

        const embedUrl = `https://www.youtube.com/embed/${videoId}`;
        console.log('URL convertida:', embedUrl);
        return embedUrl;
      }
      if (url.includes('vimeo.com')) {
        const videoId = url.split('vimeo.com/')[1]?.split('?')[0];
        const embedUrl = `https://player.vimeo.com/video/${videoId}`;
        console.log('URL convertida:', embedUrl);
        return embedUrl;
      }
      return url;
    } catch (error) {
      console.error('Erro ao processar URL:', error);
      return url;
    }
  };

  // Função para extrair título do vídeo do YouTube
  const getVideoTitle = async (url: string): Promise<string> => {
    try {
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        const videoId = url.includes('youtube.com')
          ? url.split('v=')[1]?.split('&')[0]
          : url.split('youtu.be/')[1]?.split('?')[0];

        const response = await fetch(
          `https://www.googleapis.com/youtube/v3/videos?id=${videoId}&key=AIzaSyAyTc_jYQSVM_3kg95t8ai3CWckZGG0v4c&part=snippet`
        );
        const data = await response.json();

        if (data.error) {
          console.error('Erro da API do YouTube:', data.error);
          return 'Vídeo do YouTube';
        }

        return data.items[0]?.snippet?.title || 'Vídeo do YouTube';
      }

      if (url.includes('vimeo.com')) {
        const videoId = url.split('vimeo.com/')[1]?.split('?')[0];
        const response = await fetch(`https://vimeo.com/api/v2/video/${videoId}.json`);
        const data = await response.json();
        return data[0]?.title || 'Vídeo do Vimeo';
      }

      return 'Vídeo';
    } catch (error) {
      console.error('Erro ao obter título do vídeo:', error);
      return 'Vídeo';
    }
  };

  const handleAddVideo = async (e?: React.FormEvent) => {
    if (e) e.preventDefault();
    setError('');
    setIsLoading(true);

    if (!newVideoUrl.trim()) {
      setError('Por favor, insira uma URL válida');
      setIsLoading(false);
      return;
    }

    try {
      const embedUrl = getEmbedUrl(newVideoUrl);
      const videoTitle = await getVideoTitle(newVideoUrl);

      // Adiciona o novo vídeo
      const newVideo = await videoService.addVideo(embedUrl, videoTitle);

      // Atualiza a lista localmente primeiro
      setVideos(prevVideos => [newVideo, ...prevVideos]);

      // Limpa o input e seleciona o novo vídeo
      setNewVideoUrl('');
      setSelectedVideo(newVideo);

      // Inicia a reprodução do novo vídeo
      handleVideoSelect(newVideo, true);
    } catch (error: any) {
      console.error('Erro detalhado:', error);
      setError(
        error.message || 
        error.error_description || 
        'Erro ao adicionar vídeo. Verifique se a URL é válida.'
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async (videoId: string) => {
    try {
      await videoService.deleteVideo(videoId);
      setVideos(prev => prev.filter(video => video.id !== videoId));
    } catch (error) {
      console.error('Erro ao deletar vídeo:', error);
    }
  };

  const handleVideoSelect = async (video: VideoData, shouldAutoplay: boolean = false) => {
    try {
      console.log('handleVideoSelect chamado com autoplay:', shouldAutoplay);

      // Primeiro, atualiza a ordem localmente para feedback imediato
      setVideos(prevVideos => {
        // Remove o vídeo selecionado da lista atual
        const otherVideos = prevVideos.filter(v => v.id !== video.id);
        // Coloca o vídeo selecionado no topo
        return [video, ...otherVideos];
      });

      // Atualiza no banco de dados
      await videoService.updateVideoOrder(video.id);

      // Recarrega a lista para garantir sincronização
      await loadVideos();

      const videoId = extractVideoId(video.url);
      if (!videoId) {
        console.error('VideoId não encontrado para:', video.url);
        return;
      }

      // Reproduz o vídeo
      if (player && isPlayerReady) {
        console.log('Player existente, carregando novo vídeo');
        try {
          player.loadVideoById({
            videoId: videoId,
            startSeconds: 0,
            suggestedQuality: 'large'
          });

          if (shouldAutoplay) {
            console.log('Iniciando reprodução com player existente');
            player.playVideo();
            updatePlayingState(true);
          }
        } catch (e) {
          console.error('Erro ao carregar vídeo:', e);
        }
      } else {
        console.log('Inicializando novo player');
        try {
          const newPlayer = await initializePlayer(videoId, true);
          if (newPlayer) {
            console.log('Novo player criado, iniciando reprodução');
            newPlayer.playVideo();
            updatePlayingState(true);
          }
        } catch (e) {
          console.error('Erro ao inicializar novo player:', e);
        }
      }
    } catch (error) {
      console.error('Erro em handleVideoSelect:', error);
    }
  };

  const handlePlayerReady = (event: any, shouldAutoplay: boolean = false) => {
    try {
      console.log('Player pronto, shouldAutoplay:', shouldAutoplay);
      const newPlayer = event.target;
      setPlayer(newPlayer);
      setIsPlayerReady(true);

      if (shouldAutoplay) {
        console.log('Iniciando reprodução automática');
        newPlayer.playVideo();
        updatePlayingState(true);
      }
    } catch (error) {
      console.error('Erro em handlePlayerReady:', error);
    }
  };

  const handleStateChange = (event: any) => {
    if (event.data === 0) { // vídeo terminou
      updatePlayingState(false);
      window.dispatchEvent(new Event('externalMediaStop'));
      onEnded?.();
    } else if (event.data === 1) { // vídeo começou a tocar
      updatePlayingState(true);
      window.dispatchEvent(new Event('externalMediaPlay'));
    } else if (event.data === 2) { // vídeo foi pausado
      updatePlayingState(false);
      window.dispatchEvent(new Event('externalMediaStop'));
    }
  };

  // Função para extrair videoId do YouTube
  const extractVideoId = (url: string = '') => {
    try {
      if (!url) return '';

      let videoId = '';

      if (url.includes('youtube.com/embed/')) {
        videoId = url.split('embed/')[1]?.split('?')[0];
      } else if (url.includes('youtube.com/watch')) {
        videoId = url.split('v=')[1]?.split('&')[0];
      } else if (url.includes('youtu.be/')) {
        videoId = url.split('youtu.be/')[1]?.split('?')[0];
      }

      console.log('Extraído videoId:', videoId, 'da URL:', url);
      return videoId;
    } catch (error) {
      console.error('Erro ao extrair videoId:', error);
      return '';
    }
  };

  // Adicione esta função para controlar o fade do volume do iframe
  const fadeIframeVolume = (start: number, end: number, duration: number) => {
    if (!player || !isPlayerReady) return;

    const steps = 20;
    const stepValue = (end - start) / steps;
    const stepDuration = duration / steps;
    let currentStep = 0;

    const fadeInterval = setInterval(() => {
      currentStep++;
      const newVolume = start + (stepValue * currentStep);
      const volume = Math.min(Math.max(newVolume, 0), 1) * 100;
      player.setVolume(volume);

      if (currentStep >= steps) {
        clearInterval(fadeInterval);
      }
    }, stepDuration);
  };

  // Adicione este useEffect para escutar eventos de áudio
  useEffect(() => {
    const handleAudioPlay = () => {
      if (isPlayerReady) {
        fadeIframeVolume(1, 0.10, 500);
      }
    };

    const handleAudioStop = () => {
      if (isPlayerReady) {
        fadeIframeVolume(0.10, 1, 500);
      }
    };

    window.addEventListener('audioPlay', handleAudioPlay);
    window.addEventListener('audioStop', handleAudioStop);

    return () => {
      window.removeEventListener('audioPlay', handleAudioPlay);
      window.removeEventListener('audioStop', handleAudioStop);
    };
  }, [isPlayerReady]);

  // Adicione este useEffect para monitorar mudanças no selectedVideo
  useEffect(() => {
    if (selectedVideo) {
      const videoId = extractVideoId(selectedVideo.url);
      if (videoId) {
        console.log('Detectada mudança no selectedVideo, iniciando com autoplay');
        handleVideoSelect(selectedVideo, true);
      }
    }
  }, [selectedVideo]);

  return (
    <div className="bg-[#1e1e1e] text-gray-300 rounded-lg shadow-lg p-3">
      {/* Formulário de busca */}
      <form onSubmit={handleAddVideo} className="mb-3">
        <div className="flex flex-col gap-2">
          <input
            type="url"
            value={newVideoUrl}
            onChange={(e) => setNewVideoUrl(e.target.value)}
            placeholder="Cole a URL do YouTube ou Vimeo aqui"
            className="w-full bg-[#2d2d2d] border border-[#404040] text-gray-200 rounded px-3 py-2 focus:border-[#e1aa1e] focus:outline-none"
            disabled={isLoading}
          />
          {error && <p className="text-red-400 text-sm">{error}</p>}
        </div>
      </form>

      {/* Botões de ação */}
      <div className="flex gap-2 mb-3">
        <button
          type="button"
          onClick={() => handleAddVideo()}
          className={`flex-1 ${
            isLoading 
              ? 'bg-[#e1aa1e]/50 cursor-not-allowed' 
              : 'bg-[#e1aa1e] hover:bg-[#e1aa1e]/80'
          } text-gray-900 px-3 py-1.5 rounded transition-colors flex items-center justify-center gap-2`}
          disabled={isLoading}
        >
          <svg
            className="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 4v16m8-8H4"
            />
          </svg>
          {isLoading ? 'Adicionando...' : 'Buscar Vídeo'}
        </button>

        <button
          type="button"
          onClick={() => setIsSidebarOpen(true)}
          className="flex-1 bg-[#2d2d2d] hover:bg-[#404040] border border-[#404040] text-[#e1aa1e] px-3 py-1.5 rounded flex items-center justify-center gap-2 transition-colors"
        >
          <svg
            className="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
          Últimos Vídeos
        </button>
      </div>

      {/* Área do vídeo */}
      <div>
        <div className="rounded-lg overflow-hidden border border-[#404040]">
          <div className="relative pt-[56.25%]">
            <div
              id="youtube-player"
              className="absolute top-0 left-0 w-full h-full"
            />
          </div>
        </div>
      </div>

      {/* Menu lateral */}
      <VideoSidebar
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        videos={videos}
        onVideoSelect={handleVideoSelect}
        setIsPlaying={setIsPlaying}
      />
    </div>
  );
};

export default VideoPlayer;
